import React from 'react'
import Footer from '../components/Footer'
import Navbar from '../components/Navbar'
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { Button } from '@mui/material';
import { useState, useContext } from 'react';
import { UserContext } from '../App';
import SliderSets from '../components/SliderSets'
import { SliderReps } from '../components/SliderReps'
import { useNavigate } from 'react-router-dom';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import CloseIcon from '@mui/icons-material/Close';
import TextField from '@mui/material/TextField';
import { useLocalStorage } from '../customHooks/useLocalStorage';



export default function Phase3Create() {

  const navigate = useNavigate();

  const {valueReps} = useContext(UserContext);
  const {valueSets} = useContext(UserContext);
  const {list, setList} = useContext(UserContext);
  const {routineName, setRoutineName} = useContext(UserContext);
  const {routine, setRoutine} = useContext(UserContext);
  const {allRoutines, setAllRoutines} = useContext(UserContext);


  const [day, setDay] = useLocalStorage('day',false)
  const [greenButtonClicked, setGreenButtonClicked] = useLocalStorage('greenButtonClicked',false)
  const [objectExercise, setObjectExercise] = useLocalStorage('objectExercise',null)


  const [banner, setBanner] = useState(false);
  const [nextClick, setNextClick] = useState('');
  const [open, setOpen] = useState(true);
  const [nameSaved, setNameSaved] = useState(false)
  const [dayContainsExercises, setDayContainsExercises] = useState(false)



  const handleClickDayButton = (event) => {
    let varDay = event.currentTarget.id
    if (day === event.currentTarget.id) {
      setDay(false)
    }
    else {
      setDay(event.currentTarget.id)
    }

    for (let movement in list) {
      let containsExercises = false
      for (let i = 0; i < list[movement].length; i++) {
        if (list[movement][i].day === varDay) {
          setDayContainsExercises(true)
          containsExercises = true
          break
          }
        }
        if (!containsExercises) {
          setDayContainsExercises(false)
        }
    };
  }

  const handleClickGreenButton = (event) => {
    if (greenButtonClicked !== event.currentTarget.id) {
      setGreenButtonClicked(event.currentTarget.id)
    }
    setObjectExercise({exercise: event.currentTarget.id, sets: valueSets, reps: valueReps})
  }

  const handleDiscardClick = () => {
    const newRoutine = [...routine];

    for (let i = 0; i < newRoutine.length; i++) {
      if (newRoutine[i].exercise === objectExercise.exercise) {
      newRoutine.splice(i, 1)
      setRoutine(newRoutine)
    }
  }
}

  const handleSaveClick = () => {
    let exerciseAlreadySaved = false;
    const newRoutine = [...routine];

    if (newRoutine.length === 0)
      {
        newRoutine.push(objectExercise)
        setRoutine(newRoutine)
        exerciseAlreadySaved = true;
      }
    else {
      for (let i = 0; i < newRoutine.length; i++) {
        if (newRoutine[i].exercise === objectExercise.exercise)
        {
          exerciseAlreadySaved = true;
          newRoutine[i].sets = valueSets;
          newRoutine[i].reps = valueReps;
          setRoutine(newRoutine)
          break
        } 
        else
        {
          exerciseAlreadySaved = false;
        }
      }
    }
      if (!exerciseAlreadySaved) {
        newRoutine.push(objectExercise)
        setRoutine(newRoutine)
      }
  }

  const handleNextClick = () => {
    setNextClick(true)
    const newList2 = []
    for (let movement in list) {
      for (let i = 0; i < list[movement].length; i++) {
        if (list[movement][i].isClicked) {
          newList2.push(list[movement][i])
          }
        }
        console.log(routine.length, newList2.length)
    if(routine.length === newList2.length) {
      setBanner(true)
    }
    else {
      setBanner(false)
      setOpen(true)
    }
  }
}

const closeBanner = () => {
  setBanner(false)
  setNextClick(false)
}

const handleInputChange = (event) => {
  setRoutineName(event.target.value)
}

const saveNameBanner = () => {
  const newRoutine = [...routine];
  newRoutine.unshift(routineName)
  setRoutine(newRoutine)
  setNameSaved(true)
  closeBanner()
}

const finishCreatingRoutine = () => {
  const allRoutinesNew = [...allRoutines];
  allRoutinesNew.push(routine)
  setAllRoutines(allRoutinesNew)
  setRoutine([])
  setRoutineName(false)
  const newList = {...list}
  for (let movement in newList) {
    for (let i = 0; i < newList[movement].length; i++) {
      if (newList[movement][i].isClicked) {
        newList[movement][i].isClicked = false;
        }
      if (newList[movement][i].day) {
        newList[movement][i].day = false;
        }
      }
  };
  setList(newList)
  navigate('/myRoutines')
}

const goBackFinish = () => {
  const newRoutine = [...routine];
  newRoutine.shift(routineName)
  setRoutine(newRoutine)  
  setRoutineName(false)
  setNameSaved(false)
  closeBanner()
}

  return (
    <>
    <Navbar/>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
      paddingLeft: '0em',
      marginLeft: '0em'
    }}>

      <Box sx={{ 
        padding: '1em',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        width: 'auto'}}>

          <div style={{marginTop: '5em'}}>
          <Typography variant='h2'>
            SETS AND REPS
          </Typography>
          </div>

               <Box sx={{ display: 'flex', flexDirection: 'row',
                   justifyContent: 'space-around', width: '100%',
                   backgroundColor: '#bdb9b7', borderRadius: '15px',
                   padding: '1.5em 1em 1.5em 1em', marginBottom: '4em',
                   marginTop: '2em'}}>

                    <div className='calendarColumn'>
                      <Button variant= 'buttonDay'
                          id={'monday'}
                          onClick={handleClickDayButton}>
                        Monday
                      </Button>
                    </div>

                    <div className='calendarColumn'>
                      <Button variant= 'buttonDay'
                          id={'tuesday'}
                          onClick={handleClickDayButton}>
                        Tuesday
                      </Button>
                    </div>

                    <div className='calendarColumn'>
                      <Button variant= 'buttonDay' 
                          id={'wednesday'}
                          onClick={handleClickDayButton}>
                        Wednesday
                      </Button>
                    </div>

                    <div className='calendarColumn'>
                      <Button variant= 'buttonDay'
                          id={'thursday'}
                          onClick={handleClickDayButton}>
                        Thursday
                      </Button>
                    </div>

                    <div className='calendarColumn'>
                      <Button variant= 'buttonDay' 
                          id={'friday'}
                          onClick={handleClickDayButton}>
                        Friday
                      </Button>
                    </div>

                    <div className='calendarColumn'>
                      <Button variant= 'buttonDay' 
                          id={'saturday'}
                          onClick={handleClickDayButton}>
                        Saturday
                      </Button>
                    </div>

                    <div className='calendarColumn' style={{borderRight: 'none'}}>
                      <Button variant= 'buttonDay'
                          id={'sunday'}
                          onClick={handleClickDayButton}>
                        Sunday
                       </Button>
                    </div>
              </Box>

              <div style={{display: 'flex', flexDirection: 'column', width: '100%', justifyContent: 'center', marginBottom: '2em'}}>


              <div style={{display: 'flex', width: '100%', justifyContent: 'center', marginBottom: '2em', backgroundColor: '#bdb9b7',
            borderRadius: '15px',
            padding: '1.5em 1em 1.5em 1em'}}>

              {!day && 
              <div>
                <Typography variant= 'h2'>
                  YOU HAVE NOT SELECTED ANY DAY
                </Typography>
              </div>}

              {day && dayContainsExercises &&
              <div className='columnListExercisesPushedLeft'>
                        {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) =>  
                          exercise.day === day &&
                          <Button variant= 'individualExercise' 
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                          }} onClick={handleClickGreenButton}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                        </div>}

                     {day && !greenButtonClicked &&
                      <div className='divRightContainerOfPhase3'>
                        <Typography variant='h2'>
                            YOU HAVE NOT SELECTED ANY EXERCISE
                        </Typography>
                      </div>}

                      {day && greenButtonClicked &&
                      <div className='divRightContainerOfPhase3'>
                        <div style={{display: 'flex', flexDirection: 'column', 
                        alignitems: 'center', marginBottom: '2em', backgroundColor: 'grey',
                        borderRadius: '15px',
                        padding: '3em 3em 3em 3em', width: '80%'}}>

                        <div style={{backgroundColor: 'rgb(15, 175, 15)', 
                        borderRadius: '15px',
                        padding: '3em 3em 3em 3em',
                        marginBottom: '4em',
                        display: 'flex', 
                        flexDirection: 'column', 
                        alignItems: 'center',
                        justifContent: 'center'}}>
                          <Typography 
                          sx={{fontFamily: 'Shadows Into Light, cursive', 
                          fontSize: '3em', 
                          fontWeight: '400',
                          color: '#fff',
                          marginBottom: '0.5em'}}>
                          {greenButtonClicked}</Typography>

                          <div style={{width: '100%', 
                                      display: 'flex', 
                                      flexDirection: 'column', 
                                      alignItems: 'center',
                                      justifContent: 'center',
                                      marginTop: '1em'}}>
                          <SliderSets greenButtonClicked={greenButtonClicked} 
                          setObjectExercise={setObjectExercise}/>
                          <SliderReps greenButtonClicked={greenButtonClicked} 
                          setObjectExercise={setObjectExercise}/>
                          </div>
                        </div>
                          <Box sx={{ padding: '1em',display: 'flex', flexDirection: 'row',
                            justifyContent: 'center', alignItems: 'center', width: 'auto'
                            }}>
                            <Button variant= 'contained' variant1= 'smallButton' onClick={handleDiscardClick}>
                              Discard
                            </Button>

                            <Button variant= 'contained' variant1= 'smallButton' onClick={handleSaveClick}>
                            Save
                            </Button>
                          </Box>
                        </div>
                      </div>}
              </div>
              <Box sx={{ padding: '1em',display: 'flex', flexDirection: 'row',
              justifyContent: 'center', alignItems: 'center', width: 'auto'
              }}>
                <Button variant="contained" variant1='normalButton' onClick={ () => navigate(-1)}>
                  Go Back
                </Button>

                <Button variant="contained" variant1='normalButton'  onClick={handleNextClick}>
                  Next
                </Button>
              </Box>
            </div>

      {nextClick  && !banner &&  
            <Box sx={{ 
              width: '95%',
              position: 'fixed',
              top: '70px'}}>
              <Collapse in={open}>
                <Alert variant="filled" severity="error"
                  action={
                    <IconButton
                      aria-label="close"
                      color="inherit"
                      size="small"
                      onClick={() => {
                        setOpen(false)}}
                    >
                      <CloseIcon fontSize="inherit" />
                    </IconButton>}
                  sx={{ marginBottom: 2 }}
                >
                  You must set sets and reps for each exercise
                </Alert>
                </Collapse>
              </Box>}

              {banner &&
                <div style={{
                        position: 'fixed',
                        width:'100%',
                        height: '100%',
                        backgroundColor: 'rgba(0, 0, 0, .6)',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center'
                        }}>
                          <div style={{
                            position: 'relative',
                          backgroundColor: '#efebe9',
                          borderRadius: '15px',
                          padding: '2em 2em 3em 2em',
                          marginBottom: '4em',
                          display: 'flex', 
                          flexDirection: 'column', 
                          alignItems: 'center',
                          justifContent: 'center'}}>

                            <IconButton aria-label="delete"
                            sx={{ position: 'absolute',
                              top: '5px',
                              right: '5px'}} onClick={closeBanner}>
                              <CloseIcon sx={{color: 'error'}}/>
                            </IconButton>

                            <Typography variant='h2'>
                               NAME YOUR ROUTINE
                            </Typography>

                            <TextField 
                            id="outlined-basic" 
                            label="MyRoutine"
                            variant="outlined"
                            onChange={handleInputChange}
                            sx={{width: '400px', marginTop: '3em'}}/>

                            <Box sx={{ 
                              padding: '1em',
                              display: 'flex',
                              flexDirection: 'row',
                              justifyContent: 'center',
                              alignItems: 'center',
                              width: 'auto',
                              marginTop: '2em'
                              }}>

                              <Button variant= 'contained' variant1= 'smallButton' onClick={closeBanner}>Go Back</Button>
                              <Button variant= 'contained' variant1= 'smallButton' onClick={saveNameBanner}>Save</Button>

                          </Box>
                      </div>
                  </div>
                }

                {nameSaved && 
                <div style={{
                  position: 'fixed',
                  width:'100%',
                  height: '100%',
                  backgroundColor: 'rgba(0, 0, 0, .6)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center'
                  }}>
                    <div style={{
                      position: 'relative',
                    backgroundColor: '#efebe9',
                    borderRadius: '15px',
                    padding: '2em 2em 3em 2em',
                    marginBottom: '4em',
                    display: 'flex', 
                    flexDirection: 'column', 
                    alignItems: 'center',
                    justifContent: 'center'}}>


                      <Typography variant='h2'>
                         ARE YOU SURE YOU ARE DONE CREATING YOUR ROUTINE?
                      </Typography>


                      <Box sx={{ 
                        padding: '1em',
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'center',
                        alignItems: 'center',
                        width: 'auto',
                        marginTop: '2em'
                        }}>

                        <Button variant= 'contained' variant1= 'smallButton' onClick={goBackFinish}>Go Back</Button>
                        <Button variant= 'contained' variant1= 'smallButton' onClick={finishCreatingRoutine}>Finish</Button>

                    </Box>
                </div>
            </div>
          }
                }
        </Box>
      </Container>
      <Footer/>
      </>
    )
}
