import React from 'react'
import Navbar from '../components/Navbar'
import Footer from '../components/Footer'
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import { useContext } from 'react'
import { UserContext } from '../App';
import Button from '@mui/material/Button';
import Draggable from 'react-draggable';


function DragExercises() {

  const handleClick = () =>  {}

  const {list, setList} = useContext(UserContext);

  return (
    <>
    <Navbar/>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto'
        }}>
          <Typography sx={{ 
            fontFamily: 'Shadows Into Light, cursive', 
            fontSize: '3em', 
            fontWeight: '400',
            marginTop: '1.5em',
            color: 'primary.dark',
            marginBottom: '0.5em',
            }}>
              Drag exercises</Typography>

                  <Box className='containerMovementButtons' 
                    sx={{display: 'flex',
                      flexDirection: 'row',
                      flexWrap: 'wrap',
                      marginBottom: '2em'}}>
                      {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise) => exercise.isClicked && 
                          <Draggable>
                          <Button className='buttonIndividualExercise' 
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            fontFamily: 'Lato, sans-serif',
                            fontSize: '0.8em',
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                            borderRadius: '15px',
                            padding: '1em',
                            width: '10em',
                            boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                            marginTop: '1em',
                            marginRight: '1em',
                            transition: '0.3s',
                            cursor: 'pointer',
                            '&:hover': { 
                              backgroundColor: '#bdb9b7',
                          },
                          }}
                            onClick={handleClick
                            }>
                              {exercise.title}</Button>
                              </Draggable>)}
                        </React.Fragment>)}
                    </Box>
                    <Box sx={{padding: '1em',
                     display: 'flex',
                   flexDirection: 'row',
                   juystifyContent: 'spaceBetween',
                   width: 'auto'}}>
                    <Box sx={{
                      backgroundColor: '#bdb9b7',
                      borderRadius: '15px',
                      padding: '1em',fontFamily: 'Lato, sans-serif',
                      marginRight: '1em'
                    }}>
                      Monday</Box> |
                    <Box sx={{
                      backgroundColor: '#bdb9b7',
                      borderRadius: '15px',
                      padding: '1em',fontFamily: 'Lato, sans-serif',
                    }}>Tuesday</Box> |
                    <Box sx={{
                      backgroundColor: '#bdb9b7',
                      borderRadius: '15px',
                      padding: '1em',fontFamily: 'Lato, sans-serif',
                    }}>Wednesday</Box> |
                    <Box sx={{
                      backgroundColor: '#bdb9b7',
                      borderRadius: '15px',
                      padding: '1em',fontFamily: 'Lato, sans-serif',
                    }}>Thurdsday</Box> |
                    <Box sx={{
                      backgroundColor: '#bdb9b7',
                      borderRadius: '15px',
                      padding: '1em',fontFamily: 'Lato, sans-serif',
                    }}>Friday</Box> |
                    <Box sx={{
                      backgroundColor: '#bdb9b7',
                      borderRadius: '15px',
                      padding: '1em',fontFamily: 'Lato, sans-serif',
                    }}>Saturday</Box> |
                    <Box sx={{
                      backgroundColor: '#bdb9b7',
                      borderRadius: '15px',
                      padding: '1em',fontFamily: 'Lato, sans-serif',
                    }}>Sunday</Box> |

                    </Box>
            </Box>
        </Container>
    <Footer/>
    </>
  )}


export default DragExercises
