import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import CloseIcon from '@mui/icons-material/Close';

const exercises = {
  Push: [
      { title: 'Dumbell Press', isClicked: '' },
      { title: 'Inclined Dumbell Press', isClicked: ''  },
      { title: 'Declined Dumbell Press', isClicked: ''  },
      { title: 'Bench Press', isClicked: ''  },
      { title: 'Inclined Bench Press', isClicked: ''  },
      { title: 'Declined Bench press', isClicked: ''  },
      { title: 'Dumbell flies', isClicked: ''  },
      { title: 'Inclined Dumbell Flies', isClicked: ''  },
      { title: 'Declined Dumbell Flies', isClicked: ''  },
      { title: 'Cable flies', isClicked: ''  },
      { title: 'Dumbell Military Press', isClicked: ''  },
      { title: 'Barbell Military Press', isClicked: ''  },
      { title: 'Dumbell front rise', isClicked: ''  },
      { title: 'Lateral Flies', isClicked: ''  },
      { title: 'Rope Triceps Pushdown', isClicked: ''  },
      { title: 'Barbell Triceps Pushdown', isClicked: ''  },
      { title: 'Cable Overhead Triceps Extensions', isClicked: ''  },
      { title: 'Overhead Triceps Extensions', isClicked: ''  },
      { title: 'Triceps Kickbacks', isClicked: ''  },
      { title: 'Dips', isClicked: ''  },
      { title: 'Skullcrushers', isClicked: ''  }
  ],
  Pull: [
      { title: 'Dumbell Posterior Fly ', isClicked: ''  },
      { title: 'Cable row', isClicked: ''  },
      { title: 'Machine Row', isClicked: ''  },
      { title: 'Incline Machine Row', isClicked: ''  },
      { title: 'Dumbell Row', isClicked: ''  },
      { title: 'Inclined Dumbell Row', isClicked: ''  },
      { title: 'Barbell Row', isClicked: ''  },
      { title: 'Lat Pulldown', isClicked: ''  },
      { title: 'Pull Ups', isClicked: ''  },
      { title: 'Dumbell Biceps Curl', isClicked: ''  },
      { title: 'Barbell Biceps Curl', isClicked: ''  },
      { title: 'W Dumbell Biceps Curl', isClicked: ''  },
      { title: 'W Barbell Biceps Curl', isClicked: ''  },
      { title: 'Hammer Bicep Curl', isClicked: ''  }
  ],
  Legs: [
      { title: 'Squats', isClicked: '' },
      { title: 'Quad Extension', isClicked: '' },
      { title: 'Leg Press Machine', isClicked: '' },
      { title: 'Hack Squat', isClicked: '' },
      { title: 'Bulgarian split squat', isClicked: '' },
      { title: 'Hip Thrust', isClicked: '' },
      { title: 'Deadlift', isClicked: '' },
      { title: 'Sumo Deadlift', isClicked: '' },
      { title: 'Romanian Deadlift', isClicked: '' },
      { title: 'Hamstrings curl machine', isClicked: '' }
  ]};

  const selectedExercises = []

export default function SelectExercises() {
  const navigate = useNavigate();
  const [nextClick, setNextClick] = useState('')
  const [open, setOpen] = useState(true);
  const [stateSelectedExercises, setStateSelectedExercises] = useState(0)
  const [list, setList] = useState(exercises)

  const handleClick = (title) => {
    console.log(list)
    if (!selectedExercises.includes(title)) {
      return (
        selectedExercises.push(title),
        setStateSelectedExercises(prevState => prevState + 1)
      )
    }
    else if (selectedExercises.includes(title)) {
      const exerciseIndex = selectedExercises.indexOf(title)
        return (
        selectedExercises.splice(exerciseIndex, 1),
        setStateSelectedExercises(prevState => prevState - 1)
      )
    }
    const newList = Object.keys(list).map(movement => 
      list[movement]
          .map(exercise =>
      {if (exercise.title === title) {
        const updatedExercise = {
          ...exercise,
          isClicked: !exercise.isClicked,
        };
        return updatedExercise;
      }
      return exercise;
  }));
    setList(newList)

  const handleNextClick = () => {
    setNextClick(true)
    setOpen(true)
    if (selectedExercises.length >= 1) {
      navigate('/DragExercises')
      }
    }


  return (
    <>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto'
        }}>
          <Typography sx={{ 
            fontFamily: 'Shadows Into Light, cursive', 
            fontSize: '3em', 
            fontWeight: '400',
            marginTop: '1.5em',
            color: 'primary.dark',
            marginBottom: '0.5em',
            }}>
              Choose your exercises</Typography>

              <Container maxWidth='1600' className='containerAllUls' 
              sx={{display: 'flex',
                flexDirection: 'column',
                overflow: 'hidden',
                padding: '1em'}}>
                {Object.keys(list).map(movement => 
              <React.Fragment key={movement + "Fragment"}><h2>{movement}</h2>
              <Box className='containerMovementButtons' 
              sx={{display: 'flex',
                flexDirection: 'row',
                flexWrap: 'wrap',
                marginBottom: '2em',
                padding: '0em 0em 2.3em 2.3em'}}>
                {list[movement]
                    .map(exercise => <Button className='buttonIndividualExercise' 
                    key={exercise.title}
                    id={exercise.title}
                    sx={{
                      backgroundColor: exercise.isClicked
                      ? 'rgb(15, 175, 15)'
                      : '#90caf9',
                      color: exercise.isClicked
                      ? '#fff'
                      : '#000',
                      borderRadius: '15px',
                      padding: '1em',
                      width: '10em',
                      boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                      marginTop: '1em',
                      marginRight: '1em',
                      transition: '0.3s',
                      cursor: 'pointer',
                      '&:hover': { 
                        backgroundColor: '#bdb9b7',
                    },
                    }}
                      onClick={() => handleClick(exercise.title)
                       }>
                        {exercise.title}</Button>)}
                  </Box>
                  </React.Fragment>)}
              </Container>

            <Box sx={{ padding: '1em',display: 'flex', flexDirection: 'row',
              justifyContent: 'center', alignItems: 'center', width: 'auto'
            }}>
            <Button variant="contained" className='buttonBig' 
            sx={{backgroundColor: 'primary.main',
            fontFamily: 'Shadows Into Light, cursive',
            fontSize: '3em',
            fontWeight: '500',
            width: '7em',
            borderRadius: '20px',
            marginRight: '1em',
            }} onClick={ () => navigate(-1)}>Go Back</Button>
            <Button variant="contained" className='buttonBig' 
            sx={{backgroundColor: 'primary.main',
            fontFamily: 'Shadows Into Light, cursive',
            fontSize: '3em',
            fontWeight: '500',
            width: '7em',
            borderRadius: '20px',
            marginRight: '1em',
            }} onClick={() => handleNextClick()} 
            >
            Next</Button>
            </Box>

            {selectedExercises.length < 1  && nextClick &&  
            <Box sx={{ width: '95%',
                        position: 'fixed',
                        top: '70px'}}>
              <Collapse in={open}>
                <Alert variant="filled" severity="error"
                  action={
                    <IconButton
                      aria-label="close"
                      color="inherit"
                      size="small"
                      onClick={() => {
                        setOpen('')}}
                    >
                      <CloseIcon fontSize="inherit" />
                    </IconButton>}
                  sx={{ marginBottom: 2 }}
                >
                  You must select at least one exercise to continue
                </Alert>
                </Collapse>
              </Box>}
            </Box>
          </Container>
          {selectedExercises.length >= 1 && 
            <Box 
              sx={{width: '100%', backgroundColor: '#bdb9b7', position: 'fixed', bottom
              : '30px', padding: '0.1em'}}> 
              <p>
                You have selected {stateSelectedExercises} exercises 
              </p>
              </Box>}
        </>
      )}}
