import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Slider from '@mui/material/Slider';
import MuiInput from '@mui/material/Input';
import { useContext } from 'react';
import { UserContext } from '../App';
import { Phase3Context } from '../pages/Phase3Create';

const Input = styled(MuiInput)`
  width: 42px;
  color: white;
`;

export const SliderReps = () => {
  const {valueReps, setValueReps} = useContext(UserContext);
  const {valueSets} = useContext(UserContext);

  const {greenButtonClicked} = useContext(Phase3Context)
  const {setObjectExercise} = useContext(Phase3Context)

  const handleSliderChange = (event, newValue) => {
    setValueReps(newValue);
    setObjectExercise({exercise: greenButtonClicked, sets: valueSets, reps: valueReps})
  };

  const handleInputChange = (event) => {
    setValueReps(event.target.value === '' ? '' : Number(event.target.value));
    setObjectExercise({exercise: greenButtonClicked, sets: valueSets, reps: valueReps})
  };

  const handleBlur = () => {
    if (valueReps < 0) {
      setValueReps(0);
    } else if (valueReps > 100) {
      setValueReps(100);
    }
  };

  return (
    <Box sx={{ width: 300 }}>
      <Grid container spacing={2} alignItems="center">
        <Grid item>
          <Typography id="input-slider" gutterBottom sx={{color: 'white'}}>
            Reps
          </Typography>
         </Grid>
        <Grid item xs>
          <Slider
            value={typeof valueReps === 'number' ? valueReps : 0}
            onChange={handleSliderChange}
            aria-labelledby="input-slider"
            sx={{color: 'white'}}
          />
        </Grid>
        <Grid item>
          <Input
            value={valueReps}
            size="small"
            onChange={handleInputChange}
            onBlur={handleBlur}
            inputProps={{
              step: 1,
              min: 0,
              max: 100,
              type: 'number',
              'aria-labelledby': 'input-slider',
            }}
          />
        </Grid>
      </Grid>
    </Box>
  );
}
