import React from 'react'
import { useState, useContext } from 'react';
import { UserContext } from '../App';
import {TextField, Button, Typography,Link,} from '@mui/material';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { Box, Container } from '@mui/system';
import {useNavigate } from 'react-router-dom';


const Login = () => {

    const Navigate = useNavigate()

    const {loggedIn, setLoggedIn} = useContext(UserContext)
    const {userName, setUserName} = useContext(UserContext)
    const {password, setPassword} = useContext(UserContext)


const logIn = () => {
if (userName.length >= 4 && password.length >= 6 ) {
    setLoggedIn(true)
    Navigate('/MyRoutines')
    }
}

const handleInputUserNameChange = (event) => {
    setUserName(event.target.value)
}

const handleInputPasswordChange = (event) => {
    setPassword(event.target.value)

}

    return (
        <>
        <Navbar/>
        <Container maxWidth="1600" 
        sx={{
        display: 'flex',
        flexDirection: 'column',
        height: 'auto',
        paddingLeft: '0em',
        marginLeft: '0em'
        }}>

        <Box sx={{ 
            padding: '1em',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center', 
            alignItems: 'center',
            width: 'auto',
            marginTop: '5.5em'}}>

                    <Box sx={{padding: '3em', width: '30em',
                        backgroundColor: '#fff',
                         borderRadius: '15px',
                         display: 'flex',
                         flexDirection: 'column',
                         justifyContent: 'center', 
                         alignItems: 'center'
                         }}>
                            <Typography variant= 'h2' style={{marginBottom: '0.5em'}}>Sign Up</Typography>

                        <div style={{ display: 'flex', flexDirection: 'column',
                         justifyContent: 'center', alignItems: 'center'
}}>

                            <TextField 
                            required
                            id="outlined-basic" 
                            label="UserName"
                            placeholder='Enter username'
                            variant="outlined"
                            value={userName}
                            onChange={handleInputUserNameChange}
                            error={userName.length <= 4}
                            helperText={userName.length <= 4 ? 'Your user name must have more than 4 characters' : 'Perfect'}
                            sx={{width: '400px', marginBottom: '1em'}}/>

                            <TextField 
                            required
                            id="outlined-basic" 
                            label="Password"
                            placeholder='Enter password'
                            variant="outlined"
                            type='password'
                            value={password}
                            onChange={handleInputPasswordChange}
                            error={password.length <= 6}
                            helperText={password.length <= 6 ? 'Your password must have more than 6 characters' : 'Perfect'}
                            sx={{width: '400px', marginBottom: '1em'}}/>

                        <Button type='submit'  variant= 'contained' variant1= 'smallButton' onClick={logIn}>
                            Sign Up</Button>

                        </div>

                        <Typography sx={{marginTop: '2em'}}> You have an account?
                            <Link href="/LogIn" >
                                Log In
                            </Link>
                        </Typography>

                    </Box>
            </Box>
        </Container>
        <Footer/>
        </>
    )
}

export default Login
