import React from 'react'
import Navbar from '../components/Navbar'
import Footer from '../components/Footer'
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import { useContext, useState } from 'react'
import { UserContext } from '../App';
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';
import '../App.css'
import { useNavigate }  from 'react-router-dom';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import CloseIcon from '@mui/icons-material/Close';
import { useLocalStorage } from '../customHooks/useLocalStorage';
import MapExercisesPerDay from '../components/MapExercisesPerDay'

export default function Phase2Create() {

  const navigate = useNavigate();

  const {list, setList} = useContext(UserContext);
  const [nextClick, setNextClick] = useState('');
  const [open, setOpen] = useState(true);
  const [day, setDay] = useLocalStorage('day', false);
  const [exercisesLeft, setExercisesLeft] =  useLocalStorage('exercisesLeft', true)

  const handleClickDayButton = (event) => {
    if (day === event.currentTarget.id) {
      setDay(false)
    }
    else {
      setDay(event.currentTarget.id)
    }
  }

  const hadleClickGreenButton = (movement, index) => {
    if (day) {
      const newList = {...list};
    newList[movement][index].day = day
    setList(newList)

    const newList2 = []
    for (let movement in list) {
      for (let i = 0; i < list[movement].length; i++) {
        if (list[movement][i].isClicked) {
          newList2.push(list[movement][i])
          }
        }
    };
    for (let i = 0; i < newList2.length; i++) {
      if (!newList2[i].day) {
        setExercisesLeft(true);
        break
      }
      else {
        setExercisesLeft(false);
      }
    }
    }
    else {
      return
    }
  }

  const handleNextClick = () => {
    setNextClick(true);
    setOpen(true);

    if (!exercisesLeft){
      navigate('/Phase3Create');
    }
    else {
      return
    }
  }

  return (
    <>
    <Navbar/>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto',
      marginTop: '5em'
        }}>
          <Typography variant='h2'>
               SCHEDULE YOUR TRAINING PLAN</Typography>

                  <Box className='containerMovementButtons'>
                      {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          !exercise.day &&
                          <Button variant='individualExercise'
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
