import React from 'react'
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Alert from '@mui/material/Alert';
import App from '../App.css'

const exercises = {
  Push: [
      { title: 'Dumbell Press' },
      { title: 'Inclined Dumbell Press' },
      { title: 'Declined Dumbell Press' },
      { title: 'Bench Press' },
      { title: 'Inclined Bench Press' },
      { title: 'Declined Bench press' },
      { title: 'Dumbell flies' },
      { title: 'Inclined Dumbell Flies' },
      { title: 'Declined Dumbell Flies' },
      { title: 'Cable flies' },
      { title: 'Dumbell Military Press' },
      { title: 'Barbell Military Press' },
      { title: 'Dumbell front rise' },
      { title: 'Lateral Flies' },
      { title: 'Rope Triceps Pushdown' },
      { title: 'Barbell Triceps Pushdown' },
      { title: 'Cable Overhead Triceps Extensions' },
      { title: 'Overhead Triceps Extensions' },
      { title: 'Triceps Kickbacks' },
      { title: 'Dips' },
      { title: 'Skullcrushers' }
  ],
  Pull: [
      { title: 'Dumbell Posterior Fly ' },
      { title: 'Cable row' },
      { title: 'Machine Row' },
      { title: 'Incline Machine Row' },
      { title: 'Dumbell Row' },
      { title: 'Inclined Dumbell Row' },
      { title: 'Barbell Row' },
      { title: 'Lat Pulldown' },
      { title: 'Pull Ups' },
      { title: 'Dumbell Biceps Curl' },
      { title: 'Barbell Biceps Curl' },
      { title: 'W Dumbell Biceps Curl' },
      { title: 'W Barbell Biceps Curl' },
      { title: 'Hammer Bicep Curl' }
  ],
  Legs: [
      { title: 'Squats' },
      { title: 'Quad Extension' },
      { title: 'Leg Press Machine' },
      { title: 'Hack Squat' },
      { title: 'Bulgarian split squat' },
      { title: 'Hip Thrust' },
      { title: 'Deadlift' },
      { title: 'Sumo Deadlift' },
      { title: 'Romanian Deadlift' },
      { title: 'Hamstrings curl machine' }
  ]};

  const selectedExercises = []

export default function SelectExercises() {
  const navigate = useNavigate();

  const handleClick = (event, key) => {
      if (selectedExercises.includes(key)) {
        return (
    selectedExercises.push(key),
    console.log(key),
    console.log(selectedExercises)

        )
  }

  const handleNextClick = () => {
    if (selectedExercises.length < 1) {
    <Alert severity="error">You must select at least 1 exercise to continue</Alert>
    }
    else {
      navigate('/DragExercises')
    }
  }


  return (
    <>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto'
        }}>
          <Typography sx={{ 
            fontFamily: 'Shadows Into Light, cursive', 
            fontSize: '3em', 
            fontWeight: '400',
            marginTop: '1.5em',
            color: 'primary.dark',
            marginBottom: '0.5em',
            }}>
              Choose your exercises</Typography>

              <Container maxWidth='1600' className='containerAllUls'>
                {Object.keys(exercises).map( (movement)=> 
              <><h2>{movement}</h2><Box className='containerMovementButtons'>{exercises[movement]
                    .map((exercise, key) => <Button className='buttonIndividualExercise' 
                    onClick={event => handleClick(key)}>{exercise.title}</Button>)}
                  </Box></>)}
              </Container>

            <Box sx={{ padding: '1em',display: 'flex', flexDirection: 'row',
              justifyContent: 'center', alignItems: 'center', width: 'auto'
            }}>
            <Button variant="contained" className='buttonBig' sx={{backgroundColor: 'primary.main',
    fontFamily: 'Shadows Into Light, cursive'}}>Go Back</Button>
            <Button variant="contained" className='buttonBig' onclick={handleNextClick}sx={{backgroundColor: 'primary.main',
    fontFamily: 'Shadows Into Light, cursive'}}>
            Next</Button>
            </Box>
          </Box>
      </Container>
    </>
  )}}
