import React from 'react'
import Footer from '../components/Footer'
import Navbar from '../components/Navbar'
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { Button } from '@mui/material';
import { useState, useContext } from 'react';
import { UserContext } from '../App';
import SliderSets from '../components/SliderSets'
import SliderReps from '../components/SliderReps'
import { useNavigate } from 'react-router-dom';



//modificar para que sea de 0 a 30 reps y 0 a 35 sets



export default function Phase3Create() {

  const navigate = useNavigate();


  const {list, setList} = useContext(UserContext);
  const [day, setDay] = useState(false)
  const [greenButtonClicked, setGreenButtonClicked] = useState(false)


  const handleClickDayButton = (event) => {
    if (day === event.currentTarget.id) {
      setDay(false)
    }
    else {
      setDay(event.currentTarget.id)
    }
  }

  const handleClickGreenButton = (event) => {
    if (greenButtonClicked === event.currentTarget.id) {
      setGreenButtonClicked(false)
    }
    else {
      setGreenButtonClicked(event.currentTarget.id)
    }
  }

  handleNextClick = () => {

  }

  return (
    <>
    <Navbar/>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto'
        }}>
          <Typography sx={{ 
            fontFamily: 'Shadows Into Light, cursive', 
            fontSize: '3em', 
            fontWeight: '400',
            marginTop: '1.5em',
            color: 'primary.dark',
            marginBottom: '0.5em',
            }}>
               Sets and Reps</Typography>

               <Box sx={{ display: 'flex', flexDirection: 'row',
                   justifyContent: 'space-around', width: '100%',
                   backgroundColor: '#bdb9b7', borderRadius: '15px',
                   padding: '1.5em 1em 1.5em 1em', marginBottom: '4em'}}>

                    <div className='calendarColumn'>
                      <Button 
                          sx={{color: '#fff', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', padding: '1em 2em 1em 2em',
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                              }}} 
                          id={'monday'}
                          onClick={handleClickDayButton}>
                        Monday
                      </Button>
                    </div>

                    <div className='calendarColumn'>
                      <Button 
                          sx={{color: '#fff', borderRadius: '10px', marginRight: 'auto',
                                marginLeft: 'auto', backgroundColor: '#4f5b62', padding: '1em 2em 1em 2em',
                                '&:hover': { 
                                  backgroundColor: 'rgb(15, 175, 15)',
                                }}} 
                          id={'tuesday'}
                          onClick={handleClickDayButton}>
                        Tuesday
                      </Button>
                    </div>

                    <div className='calendarColumn'>
                      <Button 
                          sx={{color: '#fff', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', padding: '1em 2em 1em 2em',
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                              }}} 
                          id={'wednesday'}
                          onClick={handleClickDayButton}>
                        Wednesday
                      </Button>
                    </div>

                    <div className='calendarColumn'>
                      <Button 
                          sx={{color: '#fff', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', padding: '1em 2em 1em 2em',
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                              }}} 
                          id={'thursday'}
                          onClick={handleClickDayButton}>
                        Thursday
                      </Button>
                    </div>

                    <div className='calendarColumn'>
                      <Button 
                          sx={{color: '#fff', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', padding: '1em 2em 1em 2em',
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                              }}} 
                          id={'friday'}
                          onClick={handleClickDayButton}>
                        Friday
                      </Button>
                    </div>

                    <div className='calendarColumn'>
                      <Button 
                          sx={{color: '#fff', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', padding: '1em 2em 1em 2em',
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                              }}} 
                          id={'saturday'}
                          onClick={handleClickDayButton}>
                        Saturday
                      </Button>
                    </div>

                    <div className='calendarColumn'>
                      <Button 
                          sx={{color: '#fff', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', padding: '1em 2em 1em 2em',
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                              }}} 
                          id={'sunday'}
                          onClick={handleClickDayButton}>
                        Sunday
                       </Button>
                    </div>
              </Box>

              <div style={{display: 'flex', width: '100%', justifyContent: 'center', marginBottom: '2em'}}>

              {!day && 
              <div><Typography sx={{ 
                fontFamily: 'Shadows Into Light, cursive', 
                fontSize: '3em', 
                fontWeight: '400',
                marginTop: '1.5em',
                color: 'primary.dark',
                marginBottom: '0.5em',
                }}>
                   You have not selected any Day</Typography></div>}

              {day &&
              <div className='columnListExercisesPushedLeft'>
                        {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) =>  
                          exercise.day === day &&
                          <Button className='buttonIndividualExercise' 
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            fontFamily: 'Lato, sans-serif',
                            fontSize: '0.8em',
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                            borderRadius: '15px',
                            padding: '1em',
                            width: '10em',
                            boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                            marginTop: '1em',
                            marginRight: 'auto',
                            marginLeft: 'auto',
                            transition: '0.3s',
                            cursor: 'pointer',
                            '&:hover': { 
                              backgroundColor: '#bdb9b7',
                          },
                          }} onClick={handleClickGreenButton}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                        </div>}

                     {day && !greenButtonClicked &&
                      <div className='divRightContainerOfPhase3'><Typography sx={{
                          fontFamily: 'Shadows Into Light, cursive', 
                          fontSize: '3em', 
                          fontWeight: '400',
                          color: 'primary.dark',

                          marginBottom: '0.5em',
                          }}>
                            You have not selected any exercise</Typography>
                      </div>}

                      {day && greenButtonClicked &&
                      <div className='divRightContainerOfPhase3'>
                        <div style={{backgroundColor: '#bdb9b7', 
                        borderRadius: '15px',
                        padding: '3em 3em 3em 3em'}}>
                          <Typography 
                          sx={{fontFamily: 'Shadows Into Light, cursive', 
                          fontSize: '3em', 
                          fontWeight: '400',
                          color: 'primary.dark',
                          marginBottom: '0.5em'}}>
                          Set reps and Sets</Typography>

                          <div style={{width: '100%', 
                                      display: 'flex', 
                                      flexDirection: 'column', 
                                      alignItems: 'center',
                                      justifContent: 'center'}}>
                          <SliderSets/>
                          <SliderReps/>
                          </div>
                        </div>
                      </div>}

                      <Box sx={{ padding: '1em',display: 'flex', flexDirection: 'row',
              justifyContent: 'center', alignItems: 'center', width: 'auto'
              }}>
                <Button variant="contained" className='buttonBig' 
                sx={{backgroundColor: 'primary.main',
                fontFamily: 'Shadows Into Light, cursive',
                fontSize: '3em',
                fontWeight: '500',
                width: '7em',
                borderRadius: '20px',
                marginRight: '1em',
                }} onClick={ () => navigate(-1)}>Go Back</Button>

                <Button variant="contained" className='buttonBig' 
                sx={{backgroundColor: 'primary.main',
                fontFamily: 'Shadows Into Light, cursive',
                fontSize: '3em',
                fontWeight: '500',
                width: '7em',
                borderRadius: '20px',
                marginRight: '1em',
                }} onClick={handleNextClick} 
                >
                Next</Button>
              </Box>

              </div>

        </Box>
      </Container>
      <Footer/>
      </>
    )
}
