import React from 'react'
import Footer from '../components/Footer'
import Navbar from '../components/Navbar'
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { Button } from '@mui/material';
import { useEffect, useState, useContext } from 'react';
import { UserContext } from '../App';
import { useNavigate } from 'react-router-dom';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import SelectExercises from './Phase1Create';



export const MyRoutines = () => {
  const {allRoutines, setAllRoutines} = useContext(UserContext);
  const {editMode, setEditMode} = useContext(UserContext)



  const navigate = useNavigate();

  useEffect(() => {
    localStorage.removeItem('selectedExercises');
    localStorage.removeItem('day');
    localStorage.removeItem('exercisesLeft');
    localStorage.removeItem('greenButtonClicked');
    localStorage.removeItem('objectExercise');
  }, []);


  const eliminateRoutine = (index) => {

    const copyAllRoutines = [...allRoutines];

    for (let routine of copyAllRoutines) {
        for (let i = 0; i < routine.length; i++) {
        if (routine[i][0] === copyAllRoutines[index][0]) {
          routine.splice(i, 1)
          setAllRoutines(copyAllRoutines)
        }
      }    
    }
  }

const handleEditButton = (index, routine) => {
  setEditMode(true)
  navigate('/Routine', {state: {index, exerciseTitle: routine[0]}})
}

  return (
    <>
    <Navbar/>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: '100vh'
          }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto',
      marginTop: '5em'
        }}>
          <Typography variant='h2'>
              MY ROUTINES
          </Typography>



            {allRoutines.length === 0 &&
            <>
            <Box className='containerMyRoutines' sx={{minWidth: '800px', padding: '4em 0 4em 0'}}>
              <Typography variant='h2'>
                You don't have a routine
              </Typography>
              <Button variant= 'contained' variant1='bigButton' href='/Phase1Create'>
                Create routine
              </Button>
            </Box>
            </>
            }


          {allRoutines.length > 0 &&
          <>
           <Box className='containerMyRoutines'>
            {allRoutines.map((routine, index) =>
            <div style={{display: 'flex', justifyContent: 'space-between',
             width: '100%',
              marginBottom: '1em',
              marginTop: '1em',
               alignItems: 'center'}}key={index}>
              <Button variant= 'containerRoutineName' onClick={() => navigate('/Routine', {state: {index, exerciseTitle: routine[0]}})}>
                  <Typography cursor='pointer' variant='h2' sx={{color: '#fff'}}>
                    {routine[0]}
                  </Typography>
              </Button>
                <div style={{marginLeft: 'auto', display: 'flex'}}>
                  <Button variant='buttonEdit' onClick={() => handleEditButton(index, routine)}>
                    <EditIcon color='secondary' sx={{height: '2em', '&:hover': { 
                color: '#000a12'
              }}}/>
                  </Button>    
                  <Button variant= 'buttonDelete' onClick={() => eliminateRoutine(index)}>
                    <DeleteIcon color='secondary' sx={{height: '2em', '&:hover': { 
                color: '#000a12'
              }}}/>
                  </Button>
                </div>
              </div>           
            )}
                <Button variant= 'buttonAdd' onClick={() => navigate('/Phase1Create')}>
                    <AddIcon color='secondary' sx={{height: '2.5em',width: '2em', '&:hover': { 
                color: '#000a12'
              }}}/>
                </Button>
              </Box>
          </>
}
      </Box>
    </Container>
    <Footer/>
    </>
    )
}
