import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import CloseIcon from '@mui/icons-material/Close';

const exercises = {
  Push: [
      { title: 'Dumbell Press' },
      { title: 'Inclined Dumbell Press' },
      { title: 'Declined Dumbell Press' },
      { title: 'Bench Press' },
      { title: 'Inclined Bench Press' },
      { title: 'Declined Bench press' },
      { title: 'Dumbell flies' },
      { title: 'Inclined Dumbell Flies' },
      { title: 'Declined Dumbell Flies' },
      { title: 'Cable flies' },
      { title: 'Dumbell Military Press' },
      { title: 'Barbell Military Press' },
      { title: 'Dumbell front rise' },
      { title: 'Lateral Flies' },
      { title: 'Rope Triceps Pushdown' },
      { title: 'Barbell Triceps Pushdown' },
      { title: 'Cable Overhead Triceps Extensions' },
      { title: 'Overhead Triceps Extensions' },
      { title: 'Triceps Kickbacks' },
      { title: 'Dips' },
      { title: 'Skullcrushers' }
  ],
  Pull: [
      { title: 'Dumbell Posterior Fly ' },
      { title: 'Cable row' },
      { title: 'Machine Row' },
      { title: 'Incline Machine Row' },
      { title: 'Dumbell Row' },
      { title: 'Inclined Dumbell Row' },
      { title: 'Barbell Row' },
      { title: 'Lat Pulldown' },
      { title: 'Pull Ups' },
      { title: 'Dumbell Biceps Curl' },
      { title: 'Barbell Biceps Curl' },
      { title: 'W Dumbell Biceps Curl' },
      { title: 'W Barbell Biceps Curl' },
      { title: 'Hammer Bicep Curl' }
  ],
  Legs: [
      { title: 'Squats' },
      { title: 'Quad Extension' },
      { title: 'Leg Press Machine' },
      { title: 'Hack Squat' },
      { title: 'Bulgarian split squat' },
      { title: 'Hip Thrust' },
      { title: 'Deadlift' },
      { title: 'Sumo Deadlift' },
      { title: 'Romanian Deadlift' },
      { title: 'Hamstrings curl machine' }
  ]};

  const selectedExercises = []

export default function SelectExercises() {
  const navigate = useNavigate();
  const [nextClick, setNextClick] = useState(false)
  const [open, setOpen] = useState(true);
  const [stateSelectedExercises, setStateSelectedExercises] = useState(0)
  const [buttonColor, setButtonColor] = useState(false)

  const handleClick = (exercise) => {
    if (!selectedExercises.includes(exercise.title)) {
      return (
        selectedExercises.push(exercise.title),
        setStateSelectedExercises(prevState => prevState + 1),
        setButtonColor(true)
      )
    }
    else if (selectedExercises.includes(exercise.title)) {
      const exerciseIndex = selectedExercises.indexOf(exercise.title)
        return (
        selectedExercises.splice(exerciseIndex, 1),
        setStateSelectedExercises(prevState => prevState - 1),
        setButtonColor(false)
        )
    }
    if (buttonColor) {

    }
  }

  const handleNextClick = () => {
    setNextClick(true)
    setOpen(true)
    if (selectedExercises.length >= 1) {
      navigate('/DragExercises')
      }
    }


  return (
    <>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto'
        }}>
          <Typography sx={{ 
            fontFamily: 'Shadows Into Light, cursive', 
            fontSize: '3em', 
            fontWeight: '400',
            marginTop: '1.5em',
            color: 'primary.dark',
            marginBottom: '0.5em',
            }}>
              Choose your exercises</Typography>

              <Container maxWidth='1600' className='containerAllUls' 
              sx={{display: 'flex',
                flexDirection: 'column',
                overflow: 'hidden',
                padding: '1em'}}>
                {Object.keys(exercises).map(movement => 
              <React.Fragment key={movement + "Fragment"}><h2>{movement}</h2>
              <Box className='containerMovementButtons' 
              sx={{display: 'flex',
                flexDirection: 'row',
                flexWrap: 'wrap',
                marginBottom: '2em',
                padding: '0em 0em 2.3em 2.3em'}}>
                {exercises[movement]
                    .map(exercise => <Button className='buttonIndividualExercise' 
                    key={exercise.title}
                    sx={{
                      backgroundColor: '#90caf9',
                      borderRadius: '15px',
                      padding: '1em',
                      width: '10em',
                      boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                      marginTop: '1em',
                      marginRight: '1em',
                      transition: '0.3s',
                      cursor: 'pointer',
                      '&:hover': { 
                        backgroundColor: '#bdb9b7',
                    },
                      '&:active': {
                        backgroundColor: 'rgb(15, 175, 15)',
                        color: '#fff'
                      }
                    }}
                      onClick={() => handleClick(exercise)}>{exercise.title}</Button>)}
                  </Box>
                  </React.Fragment>)}
              </Container>

            <Box sx={{ padding: '1em',display: 'flex', flexDirection: 'row',
              justifyContent: 'center', alignItems: 'center', width: 'auto'
            }}>
            <Button variant="contained" className='buttonBig' 
            sx={{backgroundColor: 'primary.main',
            fontFamily: 'Shadows Into Light, cursive',
            fontSize: '3em',
            fontWeight: '500',
            width: '7em',
            borderRadius: '20px',
            marginRight: '1em',
            }} onClick={ () => navigate(-1)}>Go Back</Button>
            <Button variant="contained" className='buttonBig' 
            sx={{backgroundColor: 'primary.main',
            fontFamily: 'Shadows Into Light, cursive',
            fontSize: '3em',
            fontWeight: '500',
            width: '7em',
            borderRadius: '20px',
            marginRight: '1em',
            }} onClick={() => handleNextClick()} 
            >
            Next</Button>
            </Box>

            {selectedExercises.length < 1  && nextClick &&  
            <Box sx={{ width: '95%',
                        position: 'fixed',
                        top: '70px'}}>
              <Collapse in={open}>
                <Alert variant="filled" severity="error"
                  action={
                    <IconButton
                      aria-label="close"
                      color="inherit"
                      size="small"
                      onClick={() => {
                        setOpen(false)}}
                    >
                      <CloseIcon fontSize="inherit" />
                    </IconButton>}
                  sx={{ marginBottom: 2 }}
                >
                  You must select at least one exercise to continue
                </Alert>
                </Collapse>
              </Box>}
            </Box>
          </Container>
          {selectedExercises.length >= 1 && 
            <Box 
              sx={{width: '100%', backgroundColor: '#bdb9b7', position: 'fixed', bottom
              : '30px', padding: '0.1em'}}> 
              <p>
                You have selected {stateSelectedExercises} exercises 
              </p>
              </Box>}
        </>
      )}
