import React from 'react'
import { useContext, useState } from 'react'
import { UserContext } from '../App';
import { Button } from '@mui/material';
import { RoutineContext } from '../pages/Routine';


//me funciona si saco el index de la map function pero me falta ponerle las sets y reps al banner asique si le agrego index, para localizar
//el ejercicio en el array allExercices cuando lo paso a la handleClickGreenButton function se me rompe y no me reconoce o id o target,
//o current targe, o me dice que hay un object como react children


const CalendarWithExercises = (props) => {

    const {dayString, indexOfRoutine} = props

    const {allRoutines} = useContext(UserContext)
    const {
        editMode,
        setExerciseBannerOpen,
        setExerciseClicked,
        setExerciseIndex
    } = useContext(RoutineContext)



    const handleClickBlueButton = (event, indexOfExercise) => {
        setExerciseIndex(indexOfExercise)
        setExerciseClicked(event.target.id)
        setExerciseBannerOpen(true)
    }



    return (
        <div className='calendarColumn'>

            <div className='calendarDay'>
            {dayString}
            </div>

            <div className='calendarDayExerciseContainer'>

            {allRoutines.map((routine, index) => 
            routine[0] === allRoutines[indexOfRoutine][0] &&
            <React.Fragment key={index}>
            {routine.map((exercise, indexOfExercise) =>
            exercise.day === dayString &&

                <Button className='buttonBlue' variant='individualExercise'
                key={indexOfExercise}
                id={exercise.exercise}
                className={{editMode} ?
                'buttonShake' : null }
                sx={{
                backgroundColor:'rgb(15, 175, 15)',
                color: '#fff'}}
                 onClick={event => handleClickBlueButton(event, indexOfExercise)}>
                    {exercise.exercise}
                </Button>)}
            </React.Fragment>
            )}
            </div>
        </div>
    )
}

export default CalendarWithExercises
