import React from 'react'
import Footer from '../components/Footer'
import Navbar from '../components/Navbar'
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { Button } from '@mui/material';
import { useState, useContext, useRef } from 'react';
import { UserContext } from '../App';
import { useNavigate } from 'react-router-dom';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { useTheme } from '@mui/material/styles'
import AddIcon from '@mui/icons-material/Add';



// FIXFIX LO ULTIMO CONDITIONAL RENDERING


export const MyRoutines = () => {
  const {routineName} = useContext(UserContext);
  const {allRoutines, setAllRoutines} = useContext(UserContext);


  const logAllRoutines = () => {
    console.log(allRoutines)
  }

  return (
    <>
    <Navbar/>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto',
      marginTop: '5em'
        }}>
          <Typography variant='h2'>
              MY ROUTINES
          </Typography>



            {allRoutines.length === 0 &&
            <>
            <Box className='containerMyRoutines' sx={{minWidth: '800px', padding: '4em 0 4em 0'}}>
              <Typography variant='h2'>
                You don't have a routine
              </Typography>
              <Button variant= 'contained' variant1='bigButton' href='/Phase1Create'>
                Create routine
              </Button>
            </Box>
            </>
            }


          {allRoutines.length > 0 &&

          <>
           <Box className='containerMyRoutines'>
            {allRoutines.map( routine =>
            <>
              <div className='containerIndividualRoutine'>
                  <Typography variant='h2' sx={{color: '#fff'}}>
                    {routine[0]}
                  </Typography>
                <div style={{marginLeft: 'auto'}}>
                  <Button variant='buttonEdit'>
                    <EditIcon color='secondary' sx={{height: '2em', '&:hover': { 
                color: '#000a12'
              }}}/>
                  </Button>    
                  <Button variant= 'buttonDelete'>
                    <DeleteIcon color='secondary' sx={{height: '2em', '&:hover': { 
                color: '#000a12'
              }}}/>
                  </Button>
                </div>                    
              </div>
                </>
              )}
                            <Button variant= 'buttonAdd' onClick={logAllRoutines}>
                    <AddIcon color='secondary' sx={{height: '2.5em',width: '2em', '&:hover': { 
                color: '#000a12'
              }}}/>
                </Button>
              </Box>
          </>
}
      </Box>
    </Container>
    <Footer/>
    </>
    )
}
