import React, { useState } from 'react'
import Navbar from '../components/Navbar'
import Footer from '../components/Footer'
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import { useContext } from 'react'
import { UserContext } from '../App';
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';
import '../App.css'
import { useNavigate }  from 'react-router-dom';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import CloseIcon from '@mui/icons-material/Close';

//ver que pija pasa con la fuente que no anda si la pongo en App.css

//ver display alert y next button


export default function DragExercises() {

  const navigate = useNavigate();

  const [day, setDay] = useState('')
  const {list, setList} = useContext(UserContext);
  const [nextClick, setNextClick] = useState('')
  const [open, setOpen] = useState(true);
  const [greenExercises, setGreenExercises] = useState('')
  const [exercisesLeft, setExercisesLeft] = useState('')


  const getCLickedItemsFromList = () => {
  const newList = []
  for (let movement in list) {
    for (let i = 0; i < list[movement].length; i++) {
      if (list[movement][i].isClicked === true) {
        newList.push(list[movement][i])
      }
    }
  setGreenExercises(newList)
 }
}

  const handleClickDayButton = (id) => {
    setDay(id)
    console.log(day)
    }

  const hadleClickGreenButton = (movement, index) => {
    if (day) {
      const newList = {...list};
    newList[movement][index].day = day
    setList(newList)
    }
    else {
      return
    }
  }

  const handleNextClick = () => {
    setNextClick(true)
    for (let movement in greenExercises) {
      for (let i = 0; i < list[movement].length; i++) {
        if (list[movement][i].day === '') {
          setExercisesLeft(true);
          setOpen(true)
        }
        else {
          navigate('/SetReps')
        }
      }
    }
  }

  return (
    <>
    <Navbar/>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto'
        }}>
          <Typography sx={{ 
            fontFamily: 'Shadows Into Light, cursive', 
            fontSize: '3em', 
            fontWeight: '400',
            marginTop: '1.5em',
            color: 'primary.dark',
            marginBottom: '0.5em',
            }}>
               Select exercises</Typography>

                  <Box className='containerMovementButtons'>
                      {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          !exercise.day &&
                          <Button className='buttonIndividualExercise' 
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            fontFamily: 'Lato, sans-serif',
                            fontSize: '0.8em',
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                            borderRadius: '15px',
                            padding: '1em',
                            width: '10em',
                            boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                            marginTop: '1em',
                            marginRight: '1em',
                            transition: '0.3s',
                            cursor: 'pointer',
                            '&:hover': { 
                              backgroundColor: '#bdb9b7',
                          },
                          }} onClick={() => hadleClickGreenButton(movement, index)}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                    </Box>
                    <Box sx={{ display: 'flex', flexDirection: 'row',
                   justifyContent: 'space-around', width: '100%',
                   backgroundColor: '#bdb9b7', borderRadius: '15px',
                   padding: '1.5em 1em 1.5em 1em', marginBottom: '4em'}}>
                    <div className='calendarColumn'>
                      <div className='calendarDay'>
                        Monday</div>
                        <div className='calendarDayExerciseContainer'>
                        {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          exercise.day === 'monday' &&
                          <Button className='buttonIndividualExercise' 
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            fontFamily: 'Lato, sans-serif',
                            fontSize: '0.8em',
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                            borderRadius: '15px',
                            padding: '1em',
                            width: '10em',
                            boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                            marginTop: '1em',
                            marginRight: 'auto',
                            marginLeft: 'auto',
                            transition: '0.3s',
                            cursor: 'pointer',
                            '&:hover': { 
                              backgroundColor: '#bdb9b7',
                          },
                          }} onClick={() => hadleClickGreenButton(movement, index)}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                          <Button 
                          sx={{marginTop: '1em', width: '3px', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', 
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                      }}} id={'monday'} onClick={(e) => handleClickDayButton(e.target.id)}>
                        <AddIcon sx={{ color: 'white' }}/></Button></div>
                      </div>
                      <div className='calendarColumn'>
                      <div className='calendarDay'>
                        Tuesday</div>
                        <div className='calendarDayExerciseContainer'>
                        {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          exercise.day === 'tuesday' &&
                          <Button className='buttonIndividualExercise' 
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            fontFamily: 'Lato, sans-serif',
                            fontSize: '0.8em',
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                            borderRadius: '15px',
                            padding: '1em',
                            width: '10em',
                            boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                            marginTop: '1em',
                            marginRight: 'auto',
                            marginLeft: 'auto',
                            transition: '0.3s',
                            cursor: 'pointer',
                            '&:hover': { 
                              backgroundColor: '#bdb9b7',
                          },
                          }} onClick={() => hadleClickGreenButton(movement, index)}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                          <Button 
                          sx={{marginTop: '1em', width: '3px', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', 
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                      }}} id={'tuesday'} onClick={(e) => handleClickDayButton(e.target.id)}>
                        <AddIcon sx={{ color: 'white' }}/></Button></div>
                      </div>
                      <div className='calendarColumn'>
                      <div className='calendarDay'>
                        Wednesday</div>
                        <div className='calendarDayExerciseContainer'>
                        {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          exercise.day === 'wednesday' &&
                          <Button className='buttonIndividualExercise' 
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            fontFamily: 'Lato, sans-serif',
                            fontSize: '0.8em',
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                            borderRadius: '15px',
                            padding: '1em',
                            width: '10em',
                            boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                            marginTop: '1em',
                            marginRight: 'auto',
                            marginLeft: 'auto',
                            transition: '0.3s',
                            cursor: 'pointer',
                            '&:hover': { 
                              backgroundColor: '#bdb9b7',
                          },
                          }} onClick={() => hadleClickGreenButton(movement, index)}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                          <Button 
                          sx={{marginTop: '1em', width: '3px', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', 
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                      }}} id={'wednesday'} onClick={(e) => handleClickDayButton(e.target.id)}>
                        <AddIcon sx={{ color: 'white' }}/></Button></div>
                      </div>
                      <div className='calendarColumn'>
                      <div className='calendarDay'>
                        Thursday</div>
                        <div className='calendarDayExerciseContainer'>
                        {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          exercise.day === 'thursday' &&
                          <Button className='buttonIndividualExercise' 
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            fontFamily: 'Lato, sans-serif',
                            fontSize: '0.8em',
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                            borderRadius: '15px',
                            padding: '1em',
                            width: '10em',
                            boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                            marginTop: '1em',
                            marginRight: 'auto',
                            marginLeft: 'auto',
                            transition: '0.3s',
                            cursor: 'pointer',
                            '&:hover': { 
                              backgroundColor: '#bdb9b7',
                          },
                          }} onClick={() => hadleClickGreenButton(movement, index)}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                          <Button 
                          sx={{marginTop: '1em', width: '3px', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', 
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                      }}} id={'thursday'} onClick={(e) => handleClickDayButton(e.target.id)}>
                        <AddIcon sx={{ color: 'white' }}/></Button></div>
                      </div>
                      <div className='calendarColumn'>
                      <div className='calendarDay'>
                        Friday</div>
                        <div className='calendarDayExerciseContainer'>
                        {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          exercise.day === 'friday' &&
                          <Button className='buttonIndividualExercise' 
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            fontFamily: 'Lato, sans-serif',
                            fontSize: '0.8em',
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                            borderRadius: '15px',
                            padding: '1em',
                            width: '10em',
                            boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                            marginTop: '1em',
                            marginRight: 'auto',
                            marginLeft: 'auto',
                            transition: '0.3s',
                            cursor: 'pointer',
                            '&:hover': { 
                              backgroundColor: '#bdb9b7',
                          },
                          }} onClick={() => hadleClickGreenButton(movement, index)}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                          <Button 
                          sx={{marginTop: '1em', width: '3px', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', 
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                      }}} id={'friday'} onClick={(e) => handleClickDayButton(e.target.id)}>
                        <AddIcon sx={{ color: 'white' }}/></Button></div>                      
                        </div>
                      <div className='calendarColumn'>
                      <div className='calendarDay'>
                        Saturday</div>
                        <div className='calendarDayExerciseContainer'>
                        {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          exercise.day === 'saturday' &&
                          <Button className='buttonIndividualExercise' 
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            fontFamily: 'Lato, sans-serif',
                            fontSize: '0.8em',
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                            borderRadius: '15px',
                            padding: '1em',
                            width: '10em',
                            boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                            marginTop: '1em',
                            marginRight: 'auto',
                            marginLeft: 'auto',
                            transition: '0.3s',
                            cursor: 'pointer',
                            '&:hover': { 
                              backgroundColor: '#bdb9b7',
                          },
                          }} onClick={() => hadleClickGreenButton(movement, index)}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                          <Button 
                          sx={{marginTop: '1em', width: '3px', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', 
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                      }}} id={'saturday'} onClick={(e) => handleClickDayButton(e.target.id)}>
                        <AddIcon sx={{ color: 'white' }}/></Button></div>                      
                        </div>
                      <div className='calendarColumn'>
                      <div className='calendarDay'>
                        Sunday</div>
                        <div className='calendarDayExerciseContainer' style={{borderRight: 'none'}}>
                        {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          exercise.day === 'sunday' &&
                          <Button className='buttonIndividualExercise' 
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            fontFamily: 'Lato, sans-serif',
                            fontSize: '0.8em',
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                            borderRadius: '15px',
                            padding: '1em',
                            width: '10em',
                            boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                            marginTop: '1em',
                            marginRight: 'auto',
                            marginLeft: 'auto',
                            transition: '0.3s',
                            cursor: 'pointer',
                            '&:hover': { 
                              backgroundColor: '#bdb9b7',
                          },
                          }} onClick={() => hadleClickGreenButton(movement, index)}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                          <Button 
                          sx={{marginTop: '1em', width: '3px', borderRadius: '10px', marginRight: 'auto',
                              marginLeft: 'auto', backgroundColor: '#4f5b62', 
                              '&:hover': { 
                                backgroundColor: 'rgb(15, 175, 15)',
                      }}} id={'sunday'} onClick={(e) => handleClickDayButton(e.target.id)}>
                        <AddIcon sx={{ color: 'white' }}/></Button></div>                     
                         </div>
              </Box>

              {day && 
              <Box 
              sx={{width: '30%', backgroundColor: '#bdb9b7', position: 'fixed', 
              top: '70px', padding: '0em 1em 0em 1em',
              right: '30px',
              marginLeft: 'auto',
              marginRight: 'auto', 
              marginBottom: '0.3em', borderRadius: '15px',
              fontFamily: 'Lato, sans-serif',
              fontSize: '1.3em'}}> 
              <p style={{ display: 'inline-block'}}>
                Select the exercises you want to add to <strong>{day}</strong>
              </p>
              </Box>}

              <Box sx={{ padding: '1em',display: 'flex', flexDirection: 'row',
              justifyContent: 'center', alignItems: 'center', width: 'auto'
            }}>
            <Button variant="contained" className='buttonBig' 
            sx={{backgroundColor: 'primary.main',
            fontFamily: 'Shadows Into Light, cursive',
            fontSize: '3em',
            fontWeight: '500',
            width: '7em',
            borderRadius: '20px',
            marginRight: '1em',
            }} onClick={ () => navigate(-1)}>Go Back</Button>
            <Button variant="contained" className='buttonBig' 
            sx={{backgroundColor: 'primary.main',
            fontFamily: 'Shadows Into Light, cursive',
            fontSize: '3em',
            fontWeight: '500',
            width: '7em',
            borderRadius: '20px',
            marginRight: '1em',
            }} onClick={handleNextClick} 
            >
            Next</Button>
            </Box>

            {nextClick  && exercisesLeft &&  
            <Box sx={{ width: '95%',
                        position: 'fixed',
                        top: '70px'}}>
              <Collapse in={open}>
                <Alert variant="filled" severity="error"
                  action={
                    <IconButton
                      aria-label="close"
                      color="inherit"
                      size="small"
                      onClick={() => {
                        setOpen(false)}}
                    >
                      <CloseIcon fontSize="inherit" />
                    </IconButton>}
                  sx={{ marginBottom: 2 }}
                >
                  You must select at least one exercise to continue
                </Alert>
                </Collapse>
              </Box>}

            </Box>
        </Container>
    <Footer/>
    </>
  )}
