import React from 'react'
import Footer from '../components/Footer'
import Navbar from '../components/Navbar'
import CalendarWithExercises from '../components/CalendarWithExercises'
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import EditIcon from '@mui/icons-material/Edit';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import { useContext, useState, createContext } from 'react'
import { UserContext } from '../App';
import { useLocation } from 'react-router-dom';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import SliderSets from '../components/SliderSets'
import { SliderReps } from '../components/SliderReps'
import { Input } from '@mui/material';


export const RoutineContext = createContext()

const Routine = () => {

    const {allRoutines} = useContext(UserContext)

    const location = useLocation();

    const [exerciseBannerOpen, setExerciseBannerOpen] = useState(false)
    const [exerciseClicked, setExerciseClicked] = useState(false)
    const [exerciseIndex, setExerciseIndex] = useState(false)
    const [editMode, setEditMode] = useState(false)
    const [valueInputSets, setValueInputSets] = useState(false)
    const [valueInputReps, setValueInputReps] = useState(false)




    const editionMode = () => {
        setEditMode(false)
      }

    const handleCloseGreenButton = () => {
      setExerciseClicked(false)
      setExerciseBannerOpen(false)
      }

    const handleValueInputSets = (event) => {
      setValueInputSets(event.target.value)
      if (valueInputSets < 0) {
        setValueInputSets(0);
      } else if (valueInputSets > 100) {
        setValueInputSets(100);
      }
    }

    const handleValueInputReps = (event) => {
      setValueInputReps(event.target.value)
      if (valueInputReps < 0) {
        setValueInputReps(0);
      } else if (valueInputReps > 100) {
        setValueInputReps(100);
      }
    }

  return (
    <>
      <RoutineContext.Provider value={{
        exerciseBannerOpen,
        editMode,
        setExerciseBannerOpen,
        setExerciseClicked,
        setExerciseIndex}}>
        <Navbar/>
        <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto',
        }}>

          <div style={{marginTop: '5em'}}>
            <Typography variant='h2'>
              {location.state.exerciseTitle}
            </Typography>
          </div>


               <Box sx={{ display: 'flex', flexDirection: 'row',
                   justifyContent: 'space-around', width: '100%',
                   backgroundColor: '#bdb9b7', borderRadius: '15px',
                   padding: '1.5em 1em 1.5em 1em', marginBottom: '1em',
                   marginTop: '3em'}}>


                <CalendarWithExercises dayString={'monday'} indexOfRoutine={location.state.index}/>
                <CalendarWithExercises dayString={'tuesday'}  indexOfRoutine={location.state.index}/>
                <CalendarWithExercises dayString={'wednesday'}  indexOfRoutine={location.state.index}/>
                <CalendarWithExercises dayString={'thursday'}  indexOfRoutine={location.state.index}/>
                <CalendarWithExercises dayString={'friday'}  indexOfRoutine={location.state.index}/>
                <CalendarWithExercises dayString={'saturday'}  indexOfRoutine={location.state.index}/>
                <CalendarWithExercises dayString={'sunday'}  indexOfRoutine={location.state.index}/>
              </Box>

            {!editMode &&
              <Button variant='buttonEdit' sx={{marginLeft: 'auto'}} onClick={() => setEditMode(true)}>
                    <EditIcon color='secondary' sx={{ height: '2em', '&:hover': { 
                color: '#000a12'
              }}}/>
              </Button>
            }

            {editMode && 
              <Button variant= 'contained' variant1= 'normalButton' sx={{marginLeft: 'auto', marginRight: '0em'}} onClick={editionMode}>
                  Finish Editing
              </Button>
            }

              {exerciseClicked && exerciseBannerOpen && !editMode &&
    <div style={{
            position: 'fixed',
            width:'100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, .6)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
            }}>
              <div style={{
                position: 'relative',
              backgroundColor: 'rgb(15, 175, 15)',
              color: '#fff',
              borderRadius: '15px',
              padding: '5em 5em 5em 5em',
              marginBottom: '4em',
              display: 'flex', 
              flexDirection: 'column', 
              alignItems: 'center',
              justifContent: 'center',
              minWidth: '35em'}}>

                <IconButton aria-label="delete"
                sx={{ position: 'absolute',
                  top: '5px',
                  right: '5px'}} onClick={handleCloseGreenButton}>
                  <CloseIcon sx={{color: '#fff'}}/>
                </IconButton>

                <Typography variant='h2'>
                    {exerciseClicked}
                </Typography>


                <Box sx={{ 
                  padding: '1em',
                  display: 'flex',
                  flexDirection: 'row',
                  justifyContent: 'center',
                  alignItems: 'center',
                  width: '100%',
                  marginTop: '2em'
                  }}>
                    <div style={{marginRight: 'auto'}}>
                      <Typography variant='h2'>
                        SETS: {allRoutines[location.state.index][exerciseIndex].sets}
                      </Typography>
                    </div>

                    <div style={{marginLeft: 'auto'}}>
                      <Typography variant='h2'>
                          REPS: {allRoutines[location.state.index][exerciseIndex].reps}
                      </Typography>
                    </div>
              </Box>
          </div>
      </div>
    }

    {exerciseClicked && exerciseBannerOpen && editMode &&
     <div style={{
      position: 'fixed',
      width:'100%',
      height: '100%',
      backgroundColor: 'rgba(0, 0, 0, .6)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
      }}>
       <div style={{
        position: 'relative',
       backgroundColor: 'rgb(15, 175, 15)', 
       borderRadius: '15px',
       padding: '3em 3em 3em 3em',
       marginBottom: '4em',
       display: 'flex', 
       flexDirection: 'column', 
       alignItems: 'center',
       justifContent: 'center',
       minWidth: '40em'}}>

                <IconButton aria-label="delete"
                sx={{ position: 'absolute',
                  top: '5px',
                  right: '5px'}} onClick={handleCloseGreenButton}>
                  <CloseIcon sx={{color: '#fff'}}/>
                </IconButton>

         <Typography 
         sx={{fontFamily: 'Shadows Into Light, cursive', 
         fontSize: '3em', 
         fontWeight: '400',
         color: '#fff',
         marginBottom: '0.5em'}}>
         {exerciseClicked}
         </Typography>

         <div style={{width: '100%', 
                     display: 'flex', 
                     flexDirection: 'column', 
                     alignItems: 'center',
                     justifContent: 'center',
                     marginTop: '1em'}}>

            <Box sx={{ 
                  padding: '1em',
                  display: 'flex',
                  flexDirection: 'row',
                  justifyContent: 'center',
                  alignItems: 'center',
                  width: '100%',
                  marginTop: '2em'
                  }}>
                    <div style={{marginRight: 'auto', display: 'flex'}}>
                      <Typography variant='h2' sx={{color: '#fff'}}>
                        SETS:
                      </Typography>
                      <Input
                        size="small"
                        value={valueInputSets}
                        sx={{marginLeft: '1em', color: '#fff', fontSize: '2em', alignItems: 'center', justifyContent: 'center'}}
                        onChange={handleValueInputSets}
                        inputProps={{
                          step: 1,
                          min: 0,
                          max: 100,
                          type: 'number',
                          'aria-labelledby': 'input-slider'}}/>
                    </div>

                    <div style={{marginLeft: 'auto', display: 'flex'}}>
                      <Typography variant='h2' sx={{color: '#fff'}}>
                          REPS:
                      </Typography>
                      <Input
                        size="small"
                        value={valueInputReps}
                        sx={{marginLeft: '1em', color: '#fff', fontSize: '2em', alignItems: 'center', justifyContent: 'center'}}
                        onChange={handleValueInputReps}
                        inputProps={{
                          step: 1,
                          min: 0,
                          max: 100,
                          type: 'number',
                          'aria-labelledby': 'input-slider'}}/>
                    </div>
              </Box>
         </div>
         <Button></Button>
