import React from 'react'
import { useContext, useState } from 'react'
import { UserContext } from '../App';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { Button } from '@mui/material';
import SliderSets from '../components/SliderSets'
import { SliderReps } from '../components/SliderReps'
import { useNavigate } from 'react-router-dom';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import CloseIcon from '@mui/icons-material/Close';
import TextField from '@mui/material/TextField';
import { useLocalStorage } from '../customHooks/useLocalStorage';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';

const CalendarWithExercises = (props) => {

    const {dayString, indexOfRoutine} = props

    const {allRoutines} = useContext(UserContext)
    const [exerciseBannerOpen, setExerciseBannerOpen] = useState(false)
    const [greenButtonClicked, setGreenButtonClicked] = useState(false)



    const handleClickGreenButton = (event) => {
        setGreenButtonClicked(event.target.id)
        setExerciseBannerOpen(true)
        console.log(greenButtonClicked)
        console.log(exerciseBannerOpen)

    }


    const handleCloseGreenButton = (event) => {
          setGreenButtonClicked(false)
          setExerciseBannerOpen(false)
    }


    return (
        <>
        <div className='calendarColumn'>
            <div className='calendarDay'>
            {dayString}
            </div>

            <div className='calendarDayExerciseContainer'>

            {allRoutines.map(routine => 
            routine[0] === allRoutines[indexOfRoutine][0] &&
            <React.Fragment key={'routine'+ routine + "Fragment"}>
            {routine.map(exercise =>
            exercise.day === dayString &&

                <Button variant='individualExercise'
                key={'routine' + indexOfRoutine + exercise.exercise}
                id={exercise[0]}
                sx={{
                backgroundColor: exercise.isClicked
                ? 'rgb(15, 175, 15)'
                : '#90caf9',
                color: exercise.isClicked
                ? '#fff'
                : '#000',
                }} onClick={handleClickGreenButton}>
                    {exercise.exercise}
                </Button>)}
                </React.Fragment>
            )}
            </div>
        </div>

{greenButtonClicked && exerciseBannerOpen &&
    <div style={{
            position: 'fixed',
            width:'100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, .6)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
            }}>
              <div style={{
                position: 'relative',
              backgroundColor: '#efebe9',
              borderRadius: '15px',
              padding: '2em 2em 3em 2em',
              marginBottom: '4em',
              display: 'flex', 
              flexDirection: 'column', 
              alignItems: 'center',
              justifContent: 'center'}}>

                <IconButton aria-label="delete"
                sx={{ position: 'absolute',
                  top: '5px',
                  right: '5px'}} onClick={handleCloseGreenButton}>
                  <CloseIcon sx={{color: 'error'}}/>
                </IconButton>

                <Typography variant='h2'>
                   NAME YOUR ROUTINE
                </Typography>


                <Box sx={{ 
                  padding: '1em',
                  display: 'flex',
                  flexDirection: 'row',
                  justifyContent: 'center',
                  alignItems: 'center',
                  width: 'auto',
                  marginTop: '2em'
                  }}>
              </Box>
          </div>
      </div>
    }
    </>
    )
}

export default CalendarWithExercises
