import React from "react";
import { createContext, useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import Home from "./pages/Home";
import CssBaseline from "@mui/material/CssBaseline";
import Error404 from "./pages/Error404";
import Phase1Create from "./pages/Phase1Create";
import Phase2Create from "./pages/Phase2Create";
import Phase3Create from "./pages/Phase3Create";
import { MyRoutines } from "./pages/MyRoutines";
import Routine from "./pages/Routine";
import { useLocalStorage } from "./customHooks/useLocalStorage";
import LogIn from "./pages/LogIn";
import SignUp from "./pages/SignUp";
import { AuthProvider } from "./components/Auth";
import RequireAuth from "./components/RequireAuth";
import { useMediaQuery } from "@mui/material";


const theme = createTheme({
  palette: {
    primary: {
      light: "#4f5b62",
      main: "#263238",
      dark: "#000a12",
      contrastText: "#fff",
    },
    secondary: {
      light: "#ffffff",
      main: "#efebe9",
      dark: "#bdb9b7",
      contrastText: "#000",
    },
  },
  typography: {
    h1: {
      fontFamily: "Shadows Into Light, cursive",
      fontSize: "5em",
      fontWeight: "600",
      color: "primary.dark",
      cursor: "default",
    },
    h2: {
      fontFamily: "Shadows Into Light, cursive",
      fontSize: "3em",
      fontWeight: "400",
      color: "primary.dark",
      cursor: "default",
    },
  },
  components: {
    MuiButton: {
      variants: [
        {
          props: { variant: "contained", variant1: "bigButton" },
          style: {
            backgroundColor: "primary.main",
            marginTop: "1em",
            fontFamily: "Shadows Into Light, cursive",
            fontSize: { md: "1em", lg: '3m'} , 
            fontWeight: "500",
            padding: "0 2em 0 2em",
            borderRadius: "20px",
          },
        },
        {
          props: { variant: "contained", variant1: "normalButton" },
          style: {
            backgroundColor: "primary.main",
            fontFamily: "Shadows Into Light, cursive",
            fontSize: "3em",
            fontWeight: "500",
            width: "7em",
            borderRadius: "20px",
          },
        },
        {
          props: { variant: "contained", variant1: "smallButton" },
          style: {
            backgroundColor: "primary.main",
            fontFamily: "Shadows Into Light, cursive",
            fontSize: "2em",
            fontWeight: "500",
            width: "7em",
            borderRadius: "20px",
            "&:hover": {
              backgroundColor: "rgb(15, 175, 15)",
            },
          },
        },
        {
          props: { variant: "individualExercise" },
          style: {
            fontFamily: "Lato, sans-serif",
            fontSize: "0.8em",
            borderRadius: "15px",
            padding: "1em",
            width: "10em",
            boxShadow: "10px 5px 10px rgba(0, 0, 0, 0.3)",
            marginTop: "1em",
            marginRight: "0.5em",
            marginLeft: "0.5em",
            transition: "0.3s",
            cursor: "pointer",
            "&:hover": {
              backgroundColor: "#bdb9b7",
            },
          },
        },
        {
          props: { variant: "buttonDayAdd" },
          style: {
            marginTop: "1em",
            width: "3px",
            borderRadius: "10px",
            marginRight: "auto",
            marginLeft: "auto",
            backgroundColor: "#4f5b62",
            "&:hover": {
              backgroundColor: "rgb(15, 175, 15)",
            },
          },
        },
        {
          props: { variant: "buttonDay" },
          style: {
            color: "#fff",
            borderRadius: "10px",
            marginRight: "auto",
            marginLeft: "auto",
            backgroundColor: "#4f5b62",
            padding: "1em 2em 1em 2em",
            "&:hover": {
              backgroundColor: "rgb(15, 175, 15)",
            },
          },
        },
        {
          props: { variant: "buttonEdit" },
          style: {
            height: "4.5em",
            backgroundColor: "rgb(15, 175, 15)",
            borderRadius: "50%",
            animationName: "bigger",
            animationDuration: "0.5s",
            "&:hover": {
              backgroundColor: "#ffffff",
            },
          },
        },
        {
          props: { variant: "buttonDelete" },
          style: {
            height: "4.5em",
            marginLeft: "1em",
            backgroundColor: "red",
            borderRadius: "50%",
            animationName: "bigger",
            animationDuration: "0.5s",
            "&:hover": {
              backgroundColor: "#ffffff",
            },
          },
        },
        {
          props: { variant: "buttonAdd" },
          style: {
            position: "absolute",
            right: "0",
            bottom: "0",
            marginRight: "2em",
            marginBottom: "2em",
            backgroundColor: "grey",
            borderRadius: "50%",
            animationName: "bigger",
            animationDuration: "0.5s",
            "&:hover": {
              backgroundColor: "rgb(15, 175, 15)",
            },
          },
        },
        {
          props: { variant: "containerRoutineName" },
          style: {
            padding: "1em 2em 1em 2em",
            backgroundColor: "grey",
            borderRadius: "50px",
            minWidth: "15em",
            animationName: "bigger",
            animationDuration: "0.5s",
            "&:hover": {
              backgroundColor: "rgb(15, 175, 15)",
            },
          },
        },
      ],
    },
  },
});

