import React from 'react'
import Navbar from '../components/Navbar'
import Footer from '../components/Footer'
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import { useContext, useState } from 'react'
import { UserContext } from '../App';
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';
import '../App.css'
import { useNavigate }  from 'react-router-dom';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import CloseIcon from '@mui/icons-material/Close';
import { useLocalStorage } from '../customHooks/useLocalStorage';
import MapExercisesPerDay from '../components/MapExercisesPerDay'

export default function Phase2Create() {

  const navigate = useNavigate();

  const {list, setList} = useContext(UserContext);
  const [nextClick, setNextClick] = useState('');
  const [open, setOpen] = useState(true);
  const [day, setDay] = useLocalStorage('day', false);
  const [exercisesLeft, setExercisesLeft] =  useLocalStorage('exercisesLeft', true)



  const handleNextClick = () => {
    setNextClick(true);
    setOpen(true);

    if (!exercisesLeft){
      navigate('/Phase3Create');
    }
    else {
      return
    }
  }

  return (
    <>
    <Navbar/>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto',
      marginTop: '5em'
        }}>
          <Typography variant='h2'>
               SCHEDULE YOUR TRAINING PLAN</Typography>

                  <Box className='containerMovementButtons'>
                      {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          !exercise.day &&
                          <Button variant='individualExercise'
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                          }} onClick={() => hadleClickGreenButton(movement, index)}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                    </Box>


                    <Box sx={{ display: 'flex', flexDirection: 'row',
                   justifyContent: 'space-around', width: '100%',
                   backgroundColor: '#bdb9b7', borderRadius: '15px',
                   padding: '1.5em 1em 1.5em 1em', marginBottom: '4em'}}>

                      <MapExercisesPerDay 
                      dayString='monday' 
                      day={day} 
                      setDay={setDay} 
                      exercisesLeft={exercisesLeft} 
                      setExercisesLeft={setExercisesLeft}/>


                      <div className='calendarColumn'>
                      <div className='calendarDay'>
                        Tuesday</div>
                        <div className='calendarDayExerciseContainer'>
                        {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          exercise.day === 'tuesday' &&
                          <Button variant='individualExercise'
                          key={exercise.title}
                          id={exercise.title}
                          sx={{
                            backgroundColor: exercise.isClicked
                            ? 'rgb(15, 175, 15)'
                            : '#90caf9',
                            color: exercise.isClicked
                            ? '#fff'
                            : '#000',
                          }} onClick={() => hadleClickGreenButton(movement, index)}>
                              {exercise.title}</Button>)}
                        </React.Fragment>)}
                          <Button variant='buttonDayAdd' id={'tuesday'} onClick={handleClickDayButton}>
                        <AddIcon sx={{ color: 'white' }}/></Button></div>
                      </div>


                      <div className='calendarColumn'>
                      <div className='calendarDay'>
                        Wednesday</div>
                        <div className='calendarDayExerciseContainer'>
                        {Object.keys(list).map(movement => 
                    <React.Fragment key={movement + "Fragment"}>
                      {list[movement]
                          .map((exercise, index) => exercise.isClicked && 
                          exercise.day === 'wednesday' &&
