import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import CloseIcon from '@mui/icons-material/Close';
import { useLocalStorage } from '../customHooks/useLocalStorage'
import { TextField } from '@mui/material';
import { Filter } from '@mui/icons-material';

const exercises = {
  Push: [
      { title: 'Dumbell Press', isClicked: false },
      { title: 'Inclined Dumbell Press', isClicked: false  },
      { title: 'Declined Dumbell Press', isClicked: false  },
      { title: 'Bench Press', isClicked: false  },
      { title: 'Inclined Bench Press', isClicked: false  },
      { title: 'Declined Bench press', isClicked: false  },
      { title: 'Dumbell flies', isClicked: false  },
      { title: 'Inclined Dumbell Flies', isClicked: false  },
      { title: 'Declined Dumbell Flies', isClicked: false  },
      { title: 'Cable flies', isClicked: false  },
      { title: 'Dumbell Military Press', isClicked: false  },
      { title: 'Barbell Military Press', isClicked: false  },
      { title: 'Dumbell front rise', isClicked: false  },
      { title: 'Lateral Flies', isClicked:false  },
      { title: 'Rope Triceps Pushdown', isClicked: false  },
      { title: 'Barbell Triceps Pushdown', isClicked: false  },
      { title: 'Cable Overhead Triceps Extensions', isClicked: false  },
      { title: 'Overhead Triceps Extensions', isClicked: false  },
      { title: 'Triceps Kickbacks', isClicked: false  },
      { title: 'Dips', isClicked: false  },
      { title: 'Skullcrushers', isClicked: false  }
  ],
  Pull: [
      { title: 'Dumbell Posterior Fly ', isClicked:false  },
      { title: 'Cable row', isClicked: false  },
      { title: 'Machine Row', isClicked: false  },
      { title: 'Incline Machine Row', isClicked: false  },
      { title: 'Dumbell Row', isClicked: false  },
      { title: 'Inclined Dumbell Row', isClicked: false  },
      { title: 'Barbell Row', isClicked: false  },
      { title: 'Lat Pulldown', isClicked: false  },
      { title: 'Pull Ups', isClicked: false  },
      { title: 'Dumbell Biceps Curl', isClicked: false  },
      { title: 'Barbell Biceps Curl', isClicked: false  },
      { title: 'Scott Dumbell Biceps Curl', isClicked: false  },
      { title: 'W Barbell Biceps Curl', isClicked: false  },
      { title: 'Hammer Bicep Curl', isClicked: false  }
  ],
  Legs: [
      { title: 'Squats', isClicked: false },
      { title: 'Quad Extension', isClicked: false },
      { title: 'Leg Press Machine', isClicked: false },
      { title: 'Hack Squat', isClicked: false },
      { title: 'Bulgarian split squat', isClicked: false },
      { title: 'Hip Thrust', isClicked: false },
      { title: 'Deadlift', isClicked: false },
      { title: 'Sumo Deadlift', isClicked: false },
      { title: 'Romanian Deadlift', isClicked: false },
      { title: 'Hamstrings curl machine', isClicked: false }
  ]};


export default function SelectExercises() {
  const navigate = useNavigate();
  const [nextClick, setNextClick] = useState('')
  const [open, setOpen] = useState(true);
  const [selectedExercises, setSelectedExercises] = useLocalStorage('stateSelctedExercises', 0)
  const [list, setList] = useLocalStorage('list', exercises)
  const [filter, setFilter] = useLocalStorage('filter', '')

  const handleClick = (movement, exercise, index) => {
    const newList = {...list};
    newList[movement][index].isClicked = !newList[movement][index].isClicked;
    setList(newList)
    if (list[movement][index].isClicked) {
      return (
        setSelectedExercises(selectedExercises + 1),
        console.log(selectedExercises)
      )
    }
    else if (!list[movement][index].isClicked) {
        return (
        setSelectedExercises(selectedExercises - 1),
        console.log(selectedExercises)
      )
    }
}

  const handleNextClick = () => {
    setNextClick(true)
    setOpen(true)
    if (selectedExercises >= 1) {
      navigate('/DragExercises')
      }
    }

  const eraseExercises = () => {
    setSelectedExercises(0)
    for (let movement in list) {
      for (let i = 0; i < list[movement].length; i++) {
          list[movement][i].isClicked = false
      }
    }
  }

  return (
    <>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto'
        }}>
          <Typography sx={{ 
            fontFamily: 'Shadows Into Light, cursive', 
            fontSize: '3em', 
            fontWeight: '400',
            marginTop: '1.5em',
            color: 'primary.dark',
            marginBottom: '0.5em',
            }}>
              Choose your exercises</Typography>

              <TextField id="outlined-basic" label="Search Exercises" variant="outlined" 
              sx={{height: '50px', width: '800px'}} onChange={(event) => setFilter(event.target.value)} />
              <Container maxWidth='1600' className='containerAllUls' 
              sx={{display: 'flex',
                flexDirection: 'column',
                overflow: 'hidden',
                padding: '1em'}}>
                {Object.keys(list).map(movement => 
              <React.Fragment key={movement + "Fragment"}><h2>{movement}</h2>
              <Box className='containerMovementButtons' 
              sx={{display: 'flex',
                flexDirection: 'row',
                flexWrap: 'wrap',
                marginBottom: '2em',
                padding: '0em 0em 2.3em 2.3em'}}>
                {list[movement]
                    .map((exercise, index) => exercise.title.includes(filter) && 
                    <Button className='buttonIndividualExercise' 
                    key={exercise.title}
                    id={exercise.title}
                    sx={{
                      fontFamily: 'Lato, sans-serif',
                      fontSize: '1em',
                      backgroundColor: exercise.isClicked
                      ? 'rgb(15, 175, 15)'
                      : '#90caf9',
                      color: exercise.isClicked
                      ? '#fff'
                      : '#000',
                      borderRadius: '15px',
                      padding: '1em',
                      width: '10em',
                      boxShadow:  '10px 5px 10px rgba(0, 0, 0, 0.3)',
                      marginTop: '1em',
                      marginRight: '1em',
                      transition: '0.3s',
                      cursor: 'pointer',
                      '&:hover': { 
                        backgroundColor: '#bdb9b7',
                    },
                    }}
                      onClick={() => handleClick(movement, exercise, index)
                       }>
                        {exercise.title}</Button>)}
                  </Box>
                  </React.Fragment>)}
              </Container>

            <Box sx={{ padding: '1em',display: 'flex', flexDirection: 'row',
              justifyContent: 'center', alignItems: 'center', width: 'auto'
            }}>
            <Button variant="contained" className='buttonBig' 
            sx={{backgroundColor: 'primary.main',
            fontFamily: 'Shadows Into Light, cursive',
            fontSize: '3em',
            fontWeight: '500',
            width: '7em',
            borderRadius: '20px',
            marginRight: '1em',
            }} onClick={ () => navigate(-1)}>Go Back</Button>
            <Button variant="contained" className='buttonBig' 
            sx={{backgroundColor: 'primary.main',
            fontFamily: 'Shadows Into Light, cursive',
            fontSize: '3em',
            fontWeight: '500',
            width: '7em',
            borderRadius: '20px',
            marginRight: '1em',
            }} onClick={() => handleNextClick()} 
            >
            Next</Button>
            </Box>

            {selectedExercises < 1  && nextClick &&  
            <Box sx={{ width: '95%',
                        position: 'fixed',
                        top: '70px'}}>
              <Collapse in={open}>
                <Alert variant="filled" severity="error"
                  action={
                    <IconButton
                      aria-label="close"
                      color="inherit"
                      size="small"
                      onClick={() => {
                        setOpen(false)}}
                    >
                      <CloseIcon fontSize="inherit" />
                    </IconButton>}
                  sx={{ marginBottom: 2 }}
                >
                  You must select at least one exercise to continue
                </Alert>
                </Collapse>
              </Box>}
            </Box>
          </Container>
          {selectedExercises >= 1 && 
            <Box 
              sx={{width: '30%', backgroundColor: '#bdb9b7', position: 'fixed', 
              top: '70px', padding: '0em 1em 0em 1em',
              right: '30px',
              marginLeft: 'auto',
              marginRight: 'auto', 
              marginBottom: '0.3em', borderRadius: '15px',
              fontFamily: 'Lato, sans-serif',
              fontSize: '1.3em'}}> 
              <p style={{ display: 'inline-block'}}>
                You have selected {selectedExercises} exercises 
              </p>
              </Box>}
              {selectedExercises >= 1 && 
              <Button 
              sx={{width: '13%', backgroundColor: 'RED', position: 'fixed', 
              top: '150px', padding: '0px 16px 0px 16px',
              right: '30px',
              marginLeft: 'auto',
              marginRight: 'auto', 
              marginBottom: '0.3em', borderRadius: '15px',
              fontFamily: 'Lato, sans-serif',
              fontSize: '1em',
              color: 'white',
              '&:hover': { 
                backgroundColor: '#ff5131',
            }}} onClick={eraseExercises}> 
              <p style={{ marginRight: "20px" }}>
                erase all exercises
              </p>
              <CloseIcon/>
              </Button>}
        </>
      )}
