import React from 'react'
import { useState, useContext } from 'react';
import { UserContext } from '../App';
import {TextField, Button, Typography,Link,} from '@mui/material';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { Box, Container } from '@mui/system';
import {useNavigate } from 'react-router-dom';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import CloseIcon from '@mui/icons-material/Close';
import { useAuth } from '../components/Auth';


const LogIn = () => {

    const navigate = useNavigate()

    const {loggedIn, setLoggedIn} = useContext(UserContext)
    const {userName, setUserName} = useContext(UserContext)
    const {password, setPassword} = useContext(UserContext)
    const {inputUserNameLogin, setInputUserNameLogin} = useContext(UserContext)
    const {inputPasswordLogin, setInputPasswordLogin} = useContext(UserContext)


    const [open, setOpen] = useState(false)


    const auth = useAuth()

const logIn = () => {
    if (userName === inputUserNameLogin && password === inputPasswordLogin ) {
        auth.logIn(true)
        navigate('/MyRoutines')
        }
    else {
        setOpen(true)
    }
}

const handleInputUserNameChange = (event) => {
    setInputUserNameLogin(event.target.value)
}

const handleInputPasswordChange = (event) => {
    setInputPasswordLogin(event.target.value)

}

    return (
        <>
        <Navbar/>
        <Container maxWidth="1600" 
        sx={{
        display: 'flex',
        flexDirection: 'column',
        height: 'auto',
        paddingLeft: '0em',
        marginLeft: '0em'
        }}>

        <Box sx={{ 
            padding: '1em',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center', 
            alignItems: 'center',
            width: 'auto',
            marginTop: '8em'}}>

                    <Box sx={{padding: '3em', width: '30em',
                        backgroundColor: '#fff',
                         borderRadius: '15px',
                         display: 'flex',
                         flexDirection: 'column',
                         justifyContent: 'center', 
                         alignItems: 'center'
                         }}>
                            <Typography variant= 'h2' style={{marginBottom: '0.5em'}}>Log In</Typography>

                        <div style={{ display: 'flex', flexDirection: 'column',
                         justifyContent: 'center', alignItems: 'center'
}}>

                            <TextField 
                            required
                            label="UserName"
                            placeholder='Enter username'
                            variant="outlined"
                            value={userName}
                            onChange={handleInputUserNameChange}

                            sx={{width: '400px', marginBottom: '1em'}}/>

                            <TextField 
                            required
                            label="Password"
                            placeholder='Enter password'
                            variant="outlined"
                            type='password'
                            value={password}
                            onChange={handleInputPasswordChange}
                            sx={{width: '400px', marginBottom: '1em'}}/>

                        <Button type='submit'  variant= 'contained' variant1= 'smallButton' onClick={logIn}>
                            Log In</Button>

                        </div>

                        <Typography sx={{marginTop: '3em'}}> You don't have an account?
                            <Link href="/SignUp" >
                                Sign Up 
                            </Link>
                        </Typography>

                    </Box>

            <Box sx={{ 
              width: '95%',
              position: 'fixed',
              top: '70px'}}>
              <Collapse in={open}>
                <Alert variant="filled" severity="error"
                  action={
                    <IconButton
                      aria-label="close"
                      color="inherit"
                      size="small"
                      onClick={() => {
                        setOpen(false)}}
                    >
                      <CloseIcon fontSize="inherit" />
                    </IconButton>}
                  sx={{ marginBottom: 2 }}
                >
                  Your account is not correct or it does not exist
                </Alert>
                </Collapse>
              </Box>

            </Box>
        </Container>
        <Footer/>
        </>
    )
}

export default LogIn
