import React, { useState, useContext, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import CloseIcon from '@mui/icons-material/Close';
import { Skeleton } from '@mui/material'
import { useLocalStorage} from '../customHooks/useLocalStorage'
import { TextField } from '@mui/material';
import { UserContext } from '../App';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';




export default function SelectExercises() {

  const {list, setList} = useContext(UserContext);
  const {exercises} = useContext(UserContext);


  const navigate = useNavigate();
  const [nextClick, setNextClick] = useState('')
  const [open, setOpen] = useState(true);
  const [selectedExercises, setSelectedExercises] =  useLocalStorage('selectedExercises', 0)
  const [filter, setFilter] = useState('')

  const [loading, setLoading] = useState(true)

  useEffect(() => {
      setTimeout(() => {
          setLoading(false)
      }, 2000)
  }, [])


  const handleClick = (movement, index) => {
    const newList = {...list};
    newList[movement][index].isClicked = !newList[movement][index].isClicked;
    setList(newList)
    if (list[movement][index].isClicked) {
      return (
        setSelectedExercises(selectedExercises + 1)
      )
    }
    else if (!list[movement][index].isClicked) {
        return (
        setSelectedExercises(selectedExercises - 1)
      )
    }
}

  const handleNextClick = () => {
    setNextClick(true)
    setOpen(true)
    if (selectedExercises >= 1) {
      navigate('/Phase2Create')
      }
    }

  const eraseExercises = () => {
    setSelectedExercises(0)
    for (let movement in list) {
      for (let i = 0; i < list[movement].length; i++) {
          list[movement][i].isClicked = false
      }
    }
  }

  return (
    <>
<Navbar/>
    <Container maxWidth="1600" 
    sx={{
      display: 'flex',
      flexDirection: 'column',
      height: 'auto',
    }}>
      <Box sx={{ 
        padding: '1em',
        display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: 'auto',
      marginTop: '5em'
        }}>

              {
                loading 
                ?( <>
                <div style={{display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', width: '90vw'}}>
                <Skeleton variant='text' width='20em' height='5em' borderRadius='15px' animation='wave'/>
                <Skeleton variant='text' width='50em' height='5em' borderRadius='15px' animation='wave'/>
                <div style={{marginRight: 'auto'}}>
                <Skeleton variant='text' width='10em' height='5em' borderRadius='15px' animation='wave'/>
                </div>
                  <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-between', width: '75vw', marginTop: '2em'}}>
                  <Skeleton variant='rectangular' width='9em' height='10em' borderRadius='15px' animation='wave'/>
                  <Skeleton variant='rectangular' width='9em' height='10em' borderRadius='15px' animation='wave'/>
                  <Skeleton variant='rectangular' width='9em' height='10em' borderRadius='15px' animation='wave'/>
                  <Skeleton variant='rectangular' width='9em' height='10em' borderRadius='15px' animation='wave'/>
                  <Skeleton variant='rectangular' width='9em' height='10em' borderRadius='15px' animation='wave'/>
                  <Skeleton variant='rectangular' width='9em' height='10em' borderRadius='15px' animation='wave'/>
                  </div>
                </div>
                </>)


                : ( 
                <>
          <Typography variant='h2'>
              Choose your exercises</Typography>

              <TextField id="outlined-basic" label="Search Exercises" variant="outlined" 
              sx={{height: '3em', width: '50em', marginTop: '2em'}} onChange={(event) => setFilter(
                event.target.value.toLowerCase())}/>
              <Container maxWidth='1600' className='containerAllUls' 
              sx={{display: 'flex',
                flexDirection: 'column',
                overflow: 'hidden',
                padding: '1em'}}>
                {Object.keys(list).map(movement => 
              <React.Fragment key={movement + "Fragment"}><h2>{movement}</h2>
              <Box className='containerMovementButtons' 
              sx={{display: 'flex',
                flexDirection: 'row',
                flexWrap: 'wrap',
                marginBottom: '2em',
                padding: '0em 0em 2.3em 2.3em'}}>
                {list[movement]
                    .map((exercise, index) => exercise.title.toLowerCase().includes(filter) && 
                    <Button variant='individualExercise'
                    key={exercise.title}
                    id={exercise.title}
                    sx={{
                      backgroundColor: exercise.isClicked
                      ? 'rgb(15, 175, 15)'
                      : '#90caf9',
                      color: exercise.isClicked
                      ? '#fff'
                      : '#000',
                    }}
                      onClick={() => handleClick(movement, index)
                       }>
                        {exercise.title}</Button>)}
                  </Box>
                  </React.Fragment>)}
              </Container>

            <Box sx={{ padding: '1em',display: 'flex', flexDirection: 'row',
              justifyContent: 'space-between', width: '50em'
            }}>
            <Button variant= 'contained' variant1= 'normalButton' onClick={ () => navigate(-1)}>
              Go Back</Button>
            <Button variant= 'contained' variant1= 'normalButton' onClick={() => handleNextClick()}>
            Next</Button>
            </Box>
            </>)}

            {selectedExercises < 1  && nextClick &&  
            <Box sx={{ width: '95%',
                        position: 'fixed',
                        top: '70px'}}>
              <Collapse in={open}>
                <Alert variant="filled" severity="error"
                  action={
                    <IconButton
                      aria-label="close"
                      color="inherit"
                      size="small"
                      onClick={() => {
                        setOpen(false)}}
                    >
                      <CloseIcon fontSize="inherit" />
                    </IconButton>}
                  sx={{ marginBottom: 2 }}
                >
                  You must select at least one exercise to continue
                </Alert>
                </Collapse>
              </Box>}
            </Box>
          </Container>
          {selectedExercises >= 1 && 
            <Box 
              sx={{width: '30%', backgroundColor: '#bdb9b7', position: 'fixed', 
              top: '70px', padding: '0em 1em 0em 1em',
              right: '30px',
              marginLeft: 'auto',
              marginRight: 'auto', 
              marginBottom: '0.3em', borderRadius: '15px',
              fontFamily: 'Lato, sans-serif',
              fontSize: '1.3em'}}> 
              <p style={{ display: 'inline-block'}}>
                You have selected {selectedExercises} exercises 
              </p>
              </Box>}
              {selectedExercises >= 1 && 
              <Button 
              sx={{width: '13%', backgroundColor: 'RED', position: 'fixed', 
              top: '150px', padding: '0px 16px 0px 16px',
              right: '30px',
              marginLeft: 'auto',
              marginRight: 'auto', 
              marginBottom: '0.3em', borderRadius: '15px',
              fontFamily: 'Lato, sans-serif',
              fontSize: '1em',
              color: 'white',
              '&:hover': { 
                backgroundColor: '#ff5131',
            }}} onClick={eraseExercises}> 
              <p style={{ marginRight: "20px" }}>
                Erase all exercises
              </p>
              <CloseIcon/>
              </Button>}
              <Footer/>
        </>
      )}
